import React, { createContext, useState, useEffect } from 'react';
import clientService from '../services/client.service';

export const ClientContext = createContext();

export const ClientProvider = ({ children }) => {
    const [clients, setClients] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const fetchClients = async () => {
        try {
            setLoading(true);
            setError(null);
            const clientsData = await clientService.getAllClients();
            setClients(clientsData || []); // Garantir que é um array
        } catch (err) {
            console.error('Erro ao buscar clientes:', err);
            setError(err.message || 'Erro ao carregar clientes');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchClients();
    }, []);

    const addClient = async (clientData) => {
        try {
            const response = await clientService.createClient(clientData);
            setClients((prevClients) => [...prevClients, response.data]);
        } catch (err) {
            setError(err);
        }
    };

    const updateClient = async (clientId, updatedData) => {
        try {
            const response = await clientService.updateClient(clientId, updatedData);
            setClients((prevClients) =>
                prevClients.map((client) => (client.id === clientId ? response.data : client))
            );
        } catch (err) {
            setError(err);
        }
    };

    const deleteClient = async (clientId) => {
        try {
            await clientService.deleteClient(clientId);
            setClients((prevClients) => prevClients.filter((client) => client.id !== clientId));
        } catch (err) {
            setError(err);
        }
    };

    return (
        <ClientContext.Provider value={{ 
            clients, 
            loading, 
            error, 
            fetchClients,
            addClient, 
            updateClient, 
            deleteClient 
        }}>
            {children}
        </ClientContext.Provider>
    );
};
