import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../contexts/AuthContext';
import { Link } from 'react-router-dom';

const TrainerDashboardPage = () => {
    const { user } = useContext(AuthContext);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [stats, setStats] = useState({
        totalClients: 0,
        activeClients: 0,
        totalWorkouts: 0,
        monthlyProgress: 0
    });
    const [clients, setClients] = useState([]);
    const [recentActivities, setRecentActivities] = useState([]);
    const [weeklyStats, setWeeklyStats] = useState({
        completedWorkouts: 0,
        newClients: 0,
        positiveEvaluations: 0
    });

    useEffect(() => {
        if (user && user.role === 'trainer') {
            fetchTrainerData();
        }
    }, [user]);

    const fetchTrainerData = async () => {
        try {
            const token = localStorage.getItem('token');
            
            // Buscar clientes do treinador
            const clientsResponse = await fetch('http://localhost:5000/api/trainer/my-clients', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                }
            });

            if (clientsResponse.ok) {
                const clientsData = await clientsResponse.json();
                setClients(clientsData);
            } else {
                console.error('Erro ao buscar clientes:', clientsResponse.status);
            }

            // Buscar estatísticas do dashboard
            const statsResponse = await fetch('http://localhost:5000/api/trainer/dashboard-stats', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                }
            });

            if (statsResponse.ok) {
                const statsData = await statsResponse.json();
                setStats(statsData);
            } else {
                console.error('Erro ao buscar estatísticas:', statsResponse.status);
            }

            // Buscar atividades recentes
            const activitiesResponse = await fetch('http://localhost:5000/api/trainer/recent-activities', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                }
            });

            if (activitiesResponse.ok) {
                const activitiesData = await activitiesResponse.json();
                setRecentActivities(activitiesData);
            } else {
                console.error('Erro ao buscar atividades:', activitiesResponse.status);
                // Manter atividades vazias se a API falhar
                setRecentActivities([]);
            }

            // Buscar estatísticas semanais
            const weeklyResponse = await fetch('http://localhost:5000/api/trainer/weekly-stats', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                }
            });

            if (weeklyResponse.ok) {
                const weeklyData = await weeklyResponse.json();
                setWeeklyStats(weeklyData);
            } else {
                console.error('Erro ao buscar estatísticas semanais:', weeklyResponse.status);
            }

        } catch (err) {
            setError('Erro ao carregar dados do treinador');
            console.error('Erro:', err);
        } finally {
            setLoading(false);
        }
    };

    const formatDate = (date) => {
        const now = new Date();
        const diffMs = now - date;
        const diffMins = Math.floor(diffMs / 60000);
        const diffHours = Math.floor(diffMs / 3600000);
        const diffDays = Math.floor(diffMs / 86400000);

        if (diffMins < 60) return `${diffMins}min atrás`;
        if (diffHours < 24) return `${diffHours}h atrás`;
        return `${diffDays}d atrás`;
    };

    if (!user || user.role !== 'trainer') {
        return (
            <div className="error-page">
                <h2>Acesso Negado</h2>
                <p>Esta página é exclusiva para personal trainers.</p>
            </div>
        );
    }

    if (loading) {
        return (                <div className="loading-container">
                    <div className="loading-spinner"></div>
                    <p>Carregando painel...</p>
                </div>
        );
    }

    return (
        <div className="trainer-dashboard-page">
            {/* Header */}
            <div className="dashboard-header">
                <div className="header-content">                            <div className="trainer-info">
                                <h1>Painel do Personal Trainer</h1>
                                <p className="welcome-text">
                                    Bem-vindo, <strong>{user.profile?.firstName || user.username}</strong>!
                                </p>
                                <p className="subtitle">Gerencie seus clientes e acompanhe o progresso</p>
                            </div>
                            <div className="quick-actions">
                                <Link to="/workout-plan/new" className="btn btn-primary">
                                    <span className="icon">➕</span>
                                    Novo Treino
                                </Link>
                                <Link to="/clients/new" className="btn btn-secondary">
                                    <span className="icon">👥</span>
                                    Adicionar Cliente
                                </Link>
                            </div>
                </div>
            </div>

            {error && (
                <div className="error-banner">
                    <span className="error-icon">⚠️</span>
                    {error}
                </div>
            )}

            {/* Stats Cards */}
            <div className="stats-grid">
                <div className="stat-card primary">
                    <div className="stat-icon">👥</div>
                    <div className="stat-content">
                        <h3>{stats.totalClients}</h3>
                        <p>Total de Clientes</p>
                    </div>
                </div>                        <div className="stat-card success">
                            <div className="stat-icon">✅</div>
                            <div className="stat-content">
                                <h3>{stats.activeClients}</h3>
                                <p>Clientes Ativos</p>
                            </div>
                        </div>
                        <div className="stat-card warning">
                            <div className="stat-icon">💪</div>
                            <div className="stat-content">
                                <h3>{stats.totalWorkouts}</h3>
                                <p>Treinos Criados</p>
                            </div>
                        </div>
                        <div className="stat-card info">
                            <div className="stat-icon">📈</div>
                            <div className="stat-content">
                                <h3>{stats.monthlyProgress}%</h3>
                                <p>Progresso Mensal</p>
                            </div>
                        </div>
            </div>

            {/* Main Content */}
            <div className="dashboard-content">
                {/* Clients Section */}
                <div className="section clients-section">
                    <div className="section-header">
                        <h2>Meus Clientes</h2>
                        <Link to="/clients" className="view-all-link">
                            Ver todos <span className="arrow">→</span>
                        </Link>
                    </div>
                    
                    {clients.length === 0 ? (
                        <div className="empty-state">
                            <div className="empty-icon">👥</div>
                            <h3>Nenhum cliente ainda</h3>
                            <p>Comece adicionando seus primeiros clientes para gerenciar seus treinos</p>
                            <Link to="/clients/new" className="btn btn-primary">
                                Adicionar Primeiro Cliente
                            </Link>
                        </div>
                    ) : (
                        <div className="clients-grid">
                            {clients.slice(0, 6).map(client => (
                                <div key={client._id} className="client-card">
                                    <div className="client-avatar">
                                        {client.name.charAt(0).toUpperCase()}
                                    </div>
                                    <div className="client-info">
                                        <h4>{client.name}</h4>
                                        <p className="client-email">{client.email}</p>
                                        <p className="client-phone">{client.phone}</p>
                                    </div>
                                    <div className="client-status">
                                        <span className={`status-badge ${client.isActive !== false ? 'active' : 'inactive'}`}>
                                            {client.isActive !== false ? 'Ativo' : 'Inativo'}
                                        </span>
                                    </div>
                                    <div className="client-actions">
                                        <Link 
                                            to={`/clients/${client._id}`} 
                                            className="btn btn-small btn-outline"
                                        >
                                            Ver Perfil
                                        </Link>
                                        <Link 
                                            to={`/workout-plan/new?client=${client._id}`} 
                                            className="btn btn-small btn-primary"
                                        >
                                            Novo Treino
                                        </Link>
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}
                </div>

                {/* Recent Activities */}
                <div className="section activities-section">
                    <div className="section-header">
                        <h2>Atividades Recentes</h2>
                        <Link to="/activities" className="view-all-link">
                            Ver todas <span className="arrow">→</span>
                        </Link>
                    </div>
                    
                    {recentActivities.length === 0 ? (
                        <div className="activities-empty">
                            <p>Nenhuma atividade recente encontrada.</p>
                            <p>Comece criando treinos para seus clientes!</p>
                        </div>
                    ) : (
                        <div className="activities-list">
                            {recentActivities.map(activity => (
                                <div key={activity.id} className="activity-item">
                                    <div className="activity-icon">{activity.icon}</div>
                                    <div className="activity-content">
                                        <p className="activity-description">
                                            <strong>{activity.clientName}</strong> {activity.description}
                                        </p>
                                        <span className="activity-time">{formatDate(activity.date)}</span>
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            </div>

            {/* Quick Stats */}
            <div className="quick-stats">
                <div className="quick-stat">
                    <h3>Esta Semana</h3>                            <div className="stat-items">
                                <div className="stat-item">
                                    <span className="label">Treinos Concluídos</span>
                                    <span className="value">{weeklyStats.completedWorkouts}</span>
                                </div>
                                <div className="stat-item">
                                    <span className="label">Novos Clientes</span>
                                    <span className="value">{weeklyStats.newClients}</span>
                                </div>
                                <div className="stat-item">
                                    <span className="label">Progressos Positivos</span>
                                    <span className="value">{weeklyStats.positiveEvaluations}</span>
                                </div>
                            </div>
                </div>
            </div>
        </div>
    );
};

export default TrainerDashboardPage;
