import React, { useEffect, useState } from 'react';
import * as metricsService from '../../services/metrics.service';

const MetricsHistory = ({ clientId }) => {
    const [metrics, setMetrics] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const getMetrics = async () => {
            if (!clientId) {
                setError('ID do cliente não fornecido');
                setLoading(false);
                return;
            }

            try {
                console.log('📊 Buscando métricas para cliente:', clientId);
                const data = await metricsService.getMetricsByClientId(clientId);
                console.log('📊 Métricas encontradas:', data);
                setMetrics(Array.isArray(data) ? data : data.data || []);
            } catch (err) {
                console.error('❌ Erro ao buscar métricas:', err);
                setError('Erro ao carregar histórico de métricas');
            } finally {
                setLoading(false);
            }
        };

        getMetrics();
    }, [clientId]);

    if (loading) {
        return (
            <div className="loading-container">
                <div className="loading-spinner"></div>
                <p>Carregando histórico de métricas...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div className="error-container">
                <span className="error-icon">⚠️</span>
                <p>{error}</p>
            </div>
        );
    }

    if (!metrics || metrics.length === 0) {
        return (
            <div className="empty-state">
                <div className="empty-icon">📊</div>
                <h4>Nenhuma métrica encontrada</h4>
                <p>Este cliente ainda não possui métricas registradas.</p>
            </div>
        );
    }

    return (
        <div className="metrics-history">
            <div className="metrics-table">
                <table>
                    <thead>
                        <tr>
                            <th>Data</th>
                            <th>Peso</th>
                            <th>Altura</th>
                            <th>% Gordura</th>
                            <th>% Massa Muscular</th>
                            <th>IMC</th>
                        </tr>
                    </thead>
                    <tbody>
                        {metrics.map((metric, index) => {
                            const imc = metric.weight && metric.height 
                                ? (metric.weight / ((metric.height / 100) ** 2)).toFixed(1)
                                : '-';

                            return (
                                <tr key={metric._id || index}>
                                    <td>
                                        {metric.date || metric.createdAt
                                            ? new Date(metric.date || metric.createdAt).toLocaleDateString('pt-BR')
                                            : 'Data não informada'
                                        }
                                    </td>
                                    <td>{metric.weight ? `${metric.weight} kg` : '-'}</td>
                                    <td>{metric.height ? `${metric.height} cm` : '-'}</td>
                                    <td>{metric.bodyFatPercentage || metric.bodyFat ? `${metric.bodyFatPercentage || metric.bodyFat}%` : '-'}</td>
                                    <td>{metric.muscleMassPercentage || metric.muscleMass ? `${metric.muscleMassPercentage || metric.muscleMass}%` : '-'}</td>
                                    <td>{imc}</td>
                                </tr>
                            );
                        })}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default MetricsHistory;
