import React, { useContext } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { AuthContext } from '../../contexts/AuthContext';

const TabNavigation = () => {
    const { user } = useContext(AuthContext);
    const location = useLocation();

    // Fun√ß√£o para verificar se a aba est√° ativa
    const isActive = (path) => {
        return location.pathname === path || 
               (path === '/' && (location.pathname === '/dashboard' || location.pathname === '/'));
    };

    // Definir abas baseadas no tipo de usu√°rio
    const getTabsForUser = () => {
        if (!user) return [];

        const commonTabs = [
            { path: '/', label: 'Dashboard', icon: 'üè†' }
        ];

        if (user.role === 'admin') {
            return [
                ...commonTabs,
                { path: '/admin/trainers', label: 'Treinadores', icon: 'üë®‚Äçüíº' },
                { path: '/admin/clients', label: 'Todos os Clientes', icon: 'üë•' },
                { path: '/activities', label: 'Atividades', icon: 'üìä' }
            ];
        } else if (user.role === 'trainer') {
            return [
                { path: '/trainer/dashboard', label: 'Dashboard', icon: 'üè†' },
                { path: '/clients', label: 'Meus Clientes', icon: 'üë•' },
                { path: '/activities', label: 'Atividades', icon: 'üìä' },
                { path: '/workout-plan', label: 'Planos de Treino', icon: 'üí™' }
            ];
        } else {
            return [
                ...commonTabs,
                { path: '/my-progress', label: 'Meu Progresso', icon: 'üìà' },
                { path: '/my-workouts', label: 'Meus Treinos', icon: 'üí™' },
                { path: '/my-trainer', label: 'Meu Treinador', icon: 'üë®‚Äçüíº' }
            ];
        }
    };

    const tabs = getTabsForUser();

    if (!user || tabs.length === 0) {
        return null;
    }

    return (
        <nav className="tab-navigation">
            <div className="tab-container">
                <div className="tab-list">
                    {tabs.map((tab) => (
                        <Link
                            key={tab.path}
                            to={tab.path}
                            className={`tab-item ${isActive(tab.path) ? 'active' : ''}`}
                        >
                            <span className="tab-icon">{tab.icon}</span>
                            <span className="tab-label">{tab.label}</span>
                        </Link>
                    ))}
                </div>
            </div>
        </nav>
    );
};

export default TabNavigation;
