import React, { useState, useContext } from 'react';
import { AuthContext } from '../contexts/AuthContext';
import { useHistory } from 'react-router-dom';
import { authService } from '../services/auth.service';

const LoginPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [userType, setUserType] = useState('trainer'); // 'trainer' or 'client'
    const { login } = useContext(AuthContext);
    const history = useHistory();

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const user = await login(email, password);

            // Verificar se o tipo de usu√°rio corresponde ao selecionado
            if (userType === 'trainer' && user.role !== 'trainer' && user.role !== 'admin') {
                setError('Esta conta n√£o √© de treinador. Use o login de cliente.');
                return;
            }

            if (userType === 'client' && user.role !== 'user') {
                setError('Esta conta n√£o √© de cliente. Use o login de treinador.');
                return;
            }

            history.push('/dashboard');
        } catch (err) {
            setError('Email ou senha incorretos.');
        }
    };

    return (
        <div className="login-container">
            <h2>Login</h2>

            {/* User Type Selection */}
            <div className="user-type-selection">
                <div className="user-type-options">
                    <button
                        type="button"
                        className={`user-type-btn ${userType === 'trainer' ? 'active' : ''}`}
                        onClick={() => setUserType('trainer')}
                    >
                        <div className="user-type-icon">üë®‚Äçüíº</div>
                        <span>Sou Treinador</span>
                    </button>
                    <button
                        type="button"
                        className={`user-type-btn ${userType === 'client' ? 'active' : ''}`}
                        onClick={() => setUserType('client')}
                    >
                        <div className="user-type-icon">üèÉ‚Äç‚ôÇÔ∏è</div>
                        <span>Sou Cliente</span>
                    </button>
                </div>
            </div>

            {error && <div className="error-message">{error}</div>}

            <form onSubmit={handleLogin} className="login-form">
                <div className="form-group">
                    <label htmlFor="email">Email:</label>
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="Digite seu email"
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">Senha:</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Digite sua senha"
                        required
                    />
                </div>
                <button type="submit" className="login-btn">
                    Entrar como {userType === 'trainer' ? 'Treinador' : 'Cliente'}
                </button>

                <p className="register-link">
                    N√£o tem uma conta? 
                    <button 
                        type="button" 
                        onClick={() => history.push('/register')}
                        className="link-btn"
                    >
                        Criar Conta
                    </button>
                </p>
            </form>
        </div>
    );
};

export default LoginPage;
