import React, { createContext, useState, useEffect } from 'react';
import { authService } from '../services/auth.service';
import { isTokenExpired, startTokenValidation } from '../utils/httpInterceptor';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    useEffect(() => {
        // Check if user is logged in from localStorage
        const checkAuth = () => {
            try {
                const token = localStorage.getItem('token');
                const userData = authService.getCurrentUser();

                // Verificar se token existe e n칚o est치 expirado
                if (userData && token && !isTokenExpired(token)) {
                    setUser(userData);
                    setIsAuthenticated(true);

                    // Iniciar valida칞칚o autom치tica de token apenas se usu치rio autenticado
                    startTokenValidation();
                } else {
                    // Se n칚o tem dados completos ou token expirado, limpar tudo
                    if (token && isTokenExpired(token)) {
                        console.log('游 Token expirado detectado no AuthContext. Limpando dados...');
                    }
                    authService.logout();
                    setUser(null);
                    setIsAuthenticated(false);
                }
            } catch (error) {
                console.error('Erro ao verificar autentica칞칚o:', error);
                // Em caso de erro, limpar estado
                authService.logout();
                setUser(null);
                setIsAuthenticated(false);
            }
            setLoading(false);
        };

        checkAuth();

        // Verificar autentica칞칚o periodicamente (a cada 2 minutos)
        const interval = setInterval(() => {
            const token = localStorage.getItem('token');
            if (token && isTokenExpired(token)) {
                console.log('游 Token expirado detectado na verifica칞칚o peri칩dica. Fazendo logout...');
                logout();
            }
        }, 120000); // 2 minutos

        return () => clearInterval(interval);
    }, []);

    const login = async (email, password) => {
        try {
            console.log('Login attempt with:', email);
            const userData = await authService.login(email, password);
            console.log('Login successful:', userData);
            setUser(userData);
            setIsAuthenticated(true);
            return userData;
        } catch (error) {
            console.error("Login error:", error);
            throw error;
        }
    };

    const logout = async () => {
        try {
            // Limpar dados do localStorage
            authService.logout();

            // Limpar estado do contexto
            setUser(null);
            setIsAuthenticated(false);

            console.log('Logout realizado com sucesso');
        } catch (error) {
            console.error("Logout error:", error);

            // Mesmo se houver erro, limpar o estado local
            setUser(null);
            setIsAuthenticated(false);

            throw error;
        }
    };

    const register = async (username, email, password) => {
        try {
            const response = await authService.register(username, email, password);
            // After registration, we'll need to log in
            return response;
        } catch (error) {
            console.error("Registration error:", error);
            throw error;
        }
    };

    // Helper function to check if the current user is an admin
    const isAdmin = user && user.role === 'admin';

    return (
        <AuthContext.Provider value={{ 
            user, 
            loading, 
            isAuthenticated,
            isAdmin,
            login, 
            logout, 
            register 
        }}>
            {children}
        </AuthContext.Provider>
    );
};
