import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../../contexts/AuthContext';
import axios from 'axios';

// Ícones SVG para melhor apresentação
const WorkoutIcon = ({ size = 16, color = 'currentColor' }) => (
    <svg width={size} height={size} viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M20.57,14.86L22,13.43L20.57,12L17,15.57L8.43,7L12,3.43L10.57,2L9.14,3.43L7.71,2L5.57,4.14L4.14,2.71L2.71,4.14L4.14,5.57L2,7.71L3.43,9.14L2,10.57L3.43,12L7,8.43L15.57,17L12,20.57L13.43,22L14.86,20.57L16.29,22L18.43,19.86L19.86,21.29L21.29,19.86L19.86,18.43L22,16.29L20.57,14.86Z" fill={color}/>
    </svg>
);

const CalendarIcon = ({ size = 16, color = 'currentColor' }) => (
    <svg width={size} height={size} viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M19,3H5C3.89,3 3,3.89 3,5V19A2,2 0 0,0 5,21H19A2,2 0 0,0 21,19V5C21,3.89 20.1,3 19,3M16,1V3H8V1H6V3H5A2,2 0 0,0 3,5V19A2,2 0 0,0 5,21H19A2,2 0 0,0 21,19V5A2,2 0 0,0 19,3H18V1M19,19H5V8H19V19Z" fill={color}/>
    </svg>
);

const UserIcon = ({ size = 16, color = 'currentColor' }) => (
    <svg width={size} height={size} viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M12,4A4,4 0 0,1 16,8A4,4 0 0,1 12,12A4,4 0 0,1 8,8A4,4 0 0,1 12,4M12,14C16.42,14 20,15.79 20,18V20H4V18C4,15.79 7.58,14 12,14Z" fill={color}/>
    </svg>
);

const ClockIcon = ({ size = 16, color = 'currentColor' }) => (
    <svg width={size} height={size} viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M12,2A10,10 0 0,0 2,12A10,10 0 0,0 12,22A10,10 0 0,0 22,12A10,10 0 0,0 12,2M16.2,16.2L11,13V7H12.5V12.2L17,14.9L16.2,16.2Z" fill={color}/>
    </svg>
);

const WorkoutPlan = () => {
    const { user } = useContext(AuthContext);
    const [workoutPlans, setWorkoutPlans] = useState([]);
    const [loading, setLoading] = useState(true);
    const [expandedPlan, setExpandedPlan] = useState(null);

    useEffect(() => {
        fetchWorkoutPlans();
    }, [user]);

    const fetchWorkoutPlans = async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');

            let endpoint = '';
            if (user?.role === 'trainer') {
                endpoint = 'http://localhost:5000/api/trainer/workouts';
            } else if (user?.role === 'user') {
                endpoint = 'http://localhost:5000/api/workouts/my-workouts';
            } else {
                endpoint = 'http://localhost:5000/api/workouts/';
            }

            const response = await axios.get(endpoint, {
                headers: { Authorization: `Bearer ${token}` }
            });

            console.log('Workout plans loaded:', response.data);
            setWorkoutPlans(response.data || []);
        } catch (error) {
            console.error('Erro ao carregar planos de treino:', error);
            setWorkoutPlans([]);
        } finally {
            setLoading(false);
        }
    };

    const formatDate = (dateString) => {
        if (!dateString) return 'Data não disponível';
        return new Date(dateString).toLocaleDateString('pt-BR', {
            day: '2-digit',
            month: '2-digit',
            year: 'numeric'
        });
    };

    const formatRestTime = (seconds) => {
        if (!seconds) return '60s';
        if (seconds >= 60) {
            const minutes = Math.floor(seconds / 60);
            const remainingSeconds = seconds % 60;
            return remainingSeconds > 0 ? `${minutes}m ${remainingSeconds}s` : `${minutes}m`;
        }
        return `${seconds}s`;
    };

    const togglePlanDetails = (planId) => {
        setExpandedPlan(expandedPlan === planId ? null : planId);
    };

    if (loading) {
        return (
            <div className="workout-plans-container">
                <div className="loading-container">
                    <div className="loading-spinner"></div>
                    <p>Carregando planos de treino...</p>
                </div>
            </div>
        );
    }

    return (
        <div className="workout-plans-container">
            <div className="plans-header">
                <div className="header-content">
                    <div className="header-info">
                        <WorkoutIcon size={24} color="#3B82F6" />
                        <div>
                            <h1>Planos de Treino</h1>
                            <p>
                                {user?.role === 'trainer' 
                                    ? 'Gerencie os treinos criados para seus clientes'
                                    : 'Seus planos de treino personalizados'
                                }
                            </p>
                        </div>
                    </div>
                </div>
            </div>

            <div className="plans-content">
                {workoutPlans.length === 0 ? (
                    <div className="empty-state">
                        <WorkoutIcon size={48} color="#9CA3AF" />
                        <h3>Nenhum plano de treino encontrado</h3>
                        <p>
                            {user?.role === 'trainer' 
                                ? 'Você ainda não criou nenhum plano de treino para seus clientes.'
                                : 'Seu treinador ainda não criou nenhum plano de treino para você.'
                            }
                        </p>
                    </div>
                ) : (
                    <div className="plans-list">
                        {workoutPlans.map((plan) => (
                            <div key={plan._id} className="workout-plan-card">
                                <div className="plan-header" onClick={() => togglePlanDetails(plan._id)}>
                                    <div className="plan-title">
                                        <WorkoutIcon size={20} color="#3B82F6" />
                                        <h3>{plan.name}</h3>
                                    </div>
                                    <div className="plan-meta">
                                        <div className="meta-item">
                                            <CalendarIcon size={14} color="#6B7280" />
                                            <span>Criado em {formatDate(plan.createdAt || plan.date)}</span>
                                        </div>
                                        {plan.client?.name && (
                                            <div className="meta-item">
                                                <UserIcon size={14} color="#6B7280" />
                                                <span>{plan.client.name}</span>
                                            </div>
                                        )}
                                        <div className="meta-item">
                                            <span className="exercise-count">{plan.exercises?.length || 0} exercícios</span>
                                        </div>
                                    </div>
                                    <button className="expand-button">
                                        {expandedPlan === plan._id ? '−' : '+'}
                                    </button>
                                </div>

                                {plan.description && (
                                    <div className="plan-description">
                                        <p>{plan.description}</p>
                                    </div>
                                )}

                                {expandedPlan === plan._id && (
                                    <div className="plan-details">
                                        <div className="exercises-section">
                                            <h4>Exercícios</h4>
                                            {plan.exercises && plan.exercises.length > 0 ? (
                                                <div className="exercises-list">
                                                    {plan.exercises.map((exercise, index) => (
                                                        <div key={index} className="exercise-item">
                                                            <div className="exercise-header">
                                                                <span className="exercise-number">{index + 1}</span>
                                                                <h5>{exercise.name}</h5>
                                                            </div>
                                                            <div className="exercise-details">
                                                                <div className="detail-item">
                                                                    <span className="label">Séries:</span>
                                                                    <span className="value">{exercise.sets}</span>
                                                                </div>
                                                                <div className="detail-item">
                                                                    <span className="label">Repetições:</span>
                                                                    <span className="value">{exercise.reps}</span>
                                                                </div>
                                                                <div className="detail-item">
                                                                    <span className="label">Peso:</span>
                                                                    <span className="value">{exercise.weight}kg</span>
                                                                </div>
                                                                <div className="detail-item">
                                                                    <ClockIcon size={12} color="#6B7280" />
                                                                    <span className="value">{formatRestTime(exercise.restTime)}</span>
                                                                </div>
                                                            </div>
                                                        </div>
                                                    ))}
                                                </div>
                                            ) : (
                                                <p className="no-exercises">Nenhum exercício adicionado</p>
                                            )}
                                        </div>

                                        {plan.notes && (
                                            <div className="notes-section">
                                                <h4>Observações</h4>
                                                <p>{plan.notes}</p>
                                            </div>
                                        )}
                                    </div>
                                )}
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default WorkoutPlan;
