import React, { useEffect, useState } from 'react';
import * as metricsService from '../../services/metrics.service';

const MetricsHistory = ({ clientId }) => {
    const [metrics, setMetrics] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [sortBy, setSortBy] = useState('date');
    const [sortOrder, setSortOrder] = useState('desc'); // 'asc' ou 'desc'

    useEffect(() => {
        const getMetrics = async () => {
            if (!clientId) {
                setError('ID do cliente n√£o fornecido');
                setLoading(false);
                return;
            }

            try {
                console.log('üìä Buscando m√©tricas para cliente:', clientId);
                const data = await metricsService.getMetricsByClientId(clientId);
                console.log('üìä M√©tricas encontradas:', data);
                setMetrics(Array.isArray(data) ? data : data.data || []);
            } catch (err) {
                console.error('‚ùå Erro ao buscar m√©tricas:', err);
                setError('Erro ao carregar hist√≥rico de m√©tricas');
            } finally {
                setLoading(false);
            }
        };

        getMetrics();
    }, [clientId]);

    if (loading) {
        return (
            <div className="loading-container">
                <div className="loading-spinner"></div>
                <p>Carregando hist√≥rico de m√©tricas...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div className="error-container">
                <span className="error-icon">‚ö†Ô∏è</span>
                <p>{error}</p>
            </div>
        );
    }

    if (!metrics || metrics.length === 0) {
        return (
            <div className="empty-state">
                <div className="empty-icon">üìä</div>
                <h4>Nenhuma m√©trica encontrada</h4>
                <p>Este cliente ainda n√£o possui m√©tricas registradas.</p>
                <button className="btn btn-primary">
                    <span className="icon">‚ûï</span>
                    Adicionar Primeira M√©trica
                </button>
            </div>
        );
    }

    // Calcular varia√ß√µes entre medi√ß√µes
    const calculateVariation = (current, previous, unit = '') => {
        if (!previous || !current) return null;
        const diff = current - previous;
        const isPositive = diff > 0;
        const isNegative = diff < 0;

        return {
            value: Math.abs(diff).toFixed(1),
            isPositive,
            isNegative,
            formatted: `${isPositive ? '+' : isNegative ? '-' : ''}${Math.abs(diff).toFixed(1)}${unit}`
        };
    };

    // Fun√ß√£o para classificar IMC
    const getIMCClassification = (imc) => {
        if (imc < 18.5) return { text: 'Abaixo do peso', class: 'underweight' };
        if (imc < 25) return { text: 'Peso normal', class: 'normal' };
        if (imc < 30) return { text: 'Sobrepeso', class: 'overweight' };
        return { text: 'Obesidade', class: 'obese' };
    };

    // Fun√ß√£o para ordenar m√©tricas
    const handleSort = (column) => {
        if (sortBy === column) {
            setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
        } else {
            setSortBy(column);
            setSortOrder('desc');
        }
    };

    // Ordenar m√©tricas
    const sortedMetrics = [...metrics].sort((a, b) => {
        let aValue, bValue;

        switch (sortBy) {
            case 'date':
                aValue = new Date(a.date || a.createdAt);
                bValue = new Date(b.date || b.createdAt);
                break;
            case 'weight':
                aValue = a.weight || 0;
                bValue = b.weight || 0;
                break;
            case 'height':
                aValue = a.height || 0;
                bValue = b.height || 0;
                break;
            case 'bodyFat':
                aValue = a.bodyFatPercentage || 0;
                bValue = b.bodyFatPercentage || 0;
                break;
            case 'muscle':
                aValue = a.muscleMassPercentage || 0;
                bValue = b.muscleMassPercentage || 0;
                break;
            case 'imc':
                aValue = a.weight && a.height ? (a.weight / (a.height ** 2)) : 0;
                bValue = b.weight && b.height ? (b.weight / (b.height ** 2)) : 0;
                break;
            default:
                return 0;
        }

        if (sortOrder === 'asc') {
            return aValue > bValue ? 1 : -1;
        } else {
            return aValue < bValue ? 1 : -1;
        }
    });

    // Componente para cabe√ßalho orden√°vel
    const SortableHeader = ({ column, children }) => (
        <th className={`metric-column sortable ${sortBy === column ? 'active' : ''}`} onClick={() => handleSort(column)}>
            <span className="th-content">
                {children}
                <span className="sort-indicator">
                    {sortBy === column ? (sortOrder === 'asc' ? '‚Üë' : '‚Üì') : '‚ÜïÔ∏è'}
                </span>
            </span>
        </th>
    );

    return (
        <div className="metrics-history-modern">
            <div className="metrics-header">
                <h4>üìä Hist√≥rico de M√©tricas ({metrics.length} registros)</h4>
                <div className="metrics-actions">
                    <button className="btn btn-outline">
                        <span className="icon">üìà</span>
                        Exportar
                    </button>
                    <button className="btn btn-primary">
                        <span className="icon">‚ûï</span>
                        Nova M√©trica
                    </button>
                </div>
            </div>

            <div className="modern-table-container">
                <table className="modern-metrics-table">
                    <thead>
                        <tr>
                            <SortableHeader column="date">
                                <span className="icon">üìÖ</span>
                                Data
                            </SortableHeader>
                            <SortableHeader column="weight">
                                <span className="icon">‚öñÔ∏è</span>
                                Peso (kg)
                            </SortableHeader>
                            <th className="metric-column">
                                <span className="th-content">
                                    <span className="icon">üìè</span>
                                    Altura (cm)
                                </span>
                            </th>
                            <SortableHeader column="bodyFat">
                                <span className="icon">üìâ</span>
                                Gordura (%)
                            </SortableHeader>
                            <SortableHeader column="muscle">
                                <span className="icon">üí™</span>
                                Massa Muscular (%)
                            </SortableHeader>
                            <SortableHeader column="imc">
                                <span className="icon">üéØ</span>
                                IMC
                            </SortableHeader>
                            <th className="actions-column">A√ß√µes</th>
                        </tr>
                    </thead>
                    <tbody>
                        {sortedMetrics.map((metric, index) => {
                            const previousMetric = index > 0 ? metrics[index - 1] : null;
                            const imc = metric.weight && metric.height 
                                ? (metric.weight / (metric.height ** 2))
                                : null;

                            const imcClassification = imc ? getIMCClassification(imc) : null;

                            const weightVariation = calculateVariation(metric.weight, previousMetric?.weight, 'kg');
                            const fatVariation = calculateVariation(metric.bodyFatPercentage, previousMetric?.bodyFatPercentage, '%');
                            const muscleVariation = calculateVariation(metric.muscleMassPercentage, previousMetric?.muscleMassPercentage, '%');
                            const imcVariation = calculateVariation(imc, previousMetric ? (previousMetric.weight / (previousMetric.height ** 2)) : null);

                            return (
                                <tr key={metric._id || index} className="metric-row">
                                    <td className="date-cell">
                                        <div className="date-content">
                                            <span className="date-primary">
                                                {metric.date || metric.createdAt
                                                    ? new Date(metric.date || metric.createdAt).toLocaleDateString('pt-BR')
                                                    : 'Data n√£o informada'
                                                }
                                            </span>
                                            <span className="date-secondary">
                                                {metric.date || metric.createdAt
                                                    ? new Date(metric.date || metric.createdAt).toLocaleDateString('pt-BR', { 
                                                        weekday: 'short', 
                                                        timeZone: 'America/Sao_Paulo' 
                                                    })
                                                    : ''
                                                }
                                            </span>
                                        </div>
                                    </td>
                                    <td className="metric-cell">
                                        <div className="metric-content">
                                            <span className="metric-value">
                                                {metric.weight ? `${metric.weight.toFixed(1)}` : '-'}
                                            </span>
                                            {weightVariation && (
                                                <span className={`metric-variation ${weightVariation.isPositive ? 'positive' : 'negative'}`}>
                                                    {weightVariation.formatted}
                                                </span>
                                            )}
                                        </div>
                                    </td>
                                    <td className="metric-cell">
                                        <div className="metric-content">
                                            <span className="metric-value">
                                                {metric.height ? 
                                                    metric.height > 10 ? 
                                                        `${metric.height.toFixed(0)}` : 
                                                        `${(metric.height * 100).toFixed(0)}`
                                                    : '-'
                                                }
                                            </span>
                                        </div>
                                    </td>
                                    <td className="metric-cell">
                                        <div className="metric-content">
                                            <span className="metric-value">
                                                {metric.bodyFatPercentage ? `${metric.bodyFatPercentage.toFixed(1)}` : '-'}
                                            </span>
                                            {fatVariation && (
                                                <span className={`metric-variation ${fatVariation.isNegative ? 'positive' : 'negative'}`}>
                                                    {fatVariation.formatted}
                                                </span>
                                            )}
                                        </div>
                                    </td>
                                    <td className="metric-cell">
                                        <div className="metric-content">
                                            <span className="metric-value">
                                                {metric.muscleMassPercentage ? `${metric.muscleMassPercentage.toFixed(1)}` : '-'}
                                            </span>
                                            {muscleVariation && (
                                                <span className={`metric-variation ${muscleVariation.isPositive ? 'positive' : 'negative'}`}>
                                                    {muscleVariation.formatted}
                                                </span>
                                            )}
                                        </div>
                                    </td>
                                    <td className="metric-cell">
                                        <div className="metric-content">
                                            <span className="metric-value">
                                                {imc ? imc.toFixed(1) : '-'}
                                            </span>
                                            {imcClassification && (
                                                <span className={`imc-classification ${imcClassification.class}`}>
                                                    {imcClassification.text}
                                                </span>
                                            )}
                                            {imcVariation && (
                                                <span className={`metric-variation ${imcVariation.isPositive ? 'negative' : 'positive'}`}>
                                                    {imcVariation.formatted}
                                                </span>
                                            )}
                                        </div>
                                    </td>
                                    <td className="actions-cell">
                                        <div className="action-buttons">
                                            <button className="btn-action edit" title="Editar">
                                                <span className="icon">‚úèÔ∏è</span>
                                            </button>
                                            <button className="btn-action delete" title="Excluir">
                                                <span className="icon">üóëÔ∏è</span>
                                            </button>
                                        </div>
                                    </td>
                                </tr>
                            );
                        })}
                    </tbody>
                </table>
            </div>

            {/* Resumo das m√©tricas */}
            <div className="metrics-summary">
                <div className="summary-card">
                    <div className="summary-icon">üìà</div>
                    <div className="summary-content">
                        <span className="summary-label">Total de medi√ß√µes</span>
                        <span className="summary-value">{metrics.length}</span>
                    </div>
                </div>
                <div className="summary-card">
                    <div className="summary-icon">üìÖ</div>
                    <div className="summary-content">
                        <span className="summary-label">Primeira medi√ß√£o</span>
                        <span className="summary-value">
                            {metrics.length > 0 ? new Date(metrics[0].date || metrics[0].createdAt).toLocaleDateString('pt-BR') : '-'}
                        </span>
                    </div>
                </div>
                <div className="summary-card">
                    <div className="summary-icon">üïí</div>
                    <div className="summary-content">
                        <span className="summary-label">√öltima medi√ß√£o</span>
                        <span className="summary-value">
                            {metrics.length > 0 ? new Date(metrics[metrics.length - 1].date || metrics[metrics.length - 1].createdAt).toLocaleDateString('pt-BR') : '-'}
                        </span>
                    </div>
                </div>
                <div className="summary-card">
                    <div className="summary-icon">‚è±Ô∏è</div>
                    <div className="summary-content">
                        <span className="summary-label">Per√≠odo</span>
                        <span className="summary-value">
                            {metrics.length > 1 ? 
                                `${Math.ceil((new Date(metrics[metrics.length - 1].date) - new Date(metrics[0].date)) / (1000 * 60 * 60 * 24 * 30))} meses`
                                : 'N/A'
                            }
                        </span>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default MetricsHistory;
