import React, { useState, useEffect } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import axios from 'axios';

const TrainerForm = () => {
    const history = useHistory();
    const { trainerId } = useParams();
    const isEditing = !!trainerId;

    const [formData, setFormData] = useState({
        username: '',
        email: '',
        password: '',
        confirmPassword: '',
        profile: {
            firstName: '',
            lastName: '',
            phone: '',
            specialties: [],
            experience: '',
            certification: ''
        }
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const specialtyOptions = [
        'Musculação',
        'Cardio',
        'Funcional',
        'Pilates',
        'Yoga',
        'CrossFit',
        'Natação',
        'Corrida',
        'Fisioterapia',
        'Nutrição Esportiva'
    ];

    useEffect(() => {
        if (isEditing) {
            fetchTrainer();
        }
    }, [trainerId]);

    const fetchTrainer = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get(`http://localhost:5000/api/admin/trainers/${trainerId}`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            
            const trainer = response.data;
            setFormData({
                username: trainer.username,
                email: trainer.email,
                password: '',
                confirmPassword: '',
                profile: {
                    firstName: trainer.profile?.firstName || '',
                    lastName: trainer.profile?.lastName || '',
                    phone: trainer.profile?.phone || '',
                    specialties: trainer.profile?.specialties || [],
                    experience: trainer.profile?.experience || '',
                    certification: trainer.profile?.certification || ''
                }
            });
        } catch (error) {
            console.error('Error fetching trainer:', error);
            setError('Erro ao carregar dados do personal');
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        
        if (name.startsWith('profile.')) {
            const profileField = name.replace('profile.', '');
            setFormData(prev => ({
                ...prev,
                profile: {
                    ...prev.profile,
                    [profileField]: value
                }
            }));
        } else {
            setFormData(prev => ({
                ...prev,
                [name]: value
            }));
        }
    };

    const handleSpecialtyChange = (specialty) => {
        setFormData(prev => ({
            ...prev,
            profile: {
                ...prev.profile,
                specialties: prev.profile.specialties.includes(specialty)
                    ? prev.profile.specialties.filter(s => s !== specialty)
                    : [...prev.profile.specialties, specialty]
            }
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        // Validations
        if (!isEditing && formData.password !== formData.confirmPassword) {
            setError('As senhas não coincidem');
            setLoading(false);
            return;
        }

        if (!isEditing && formData.password.length < 6) {
            setError('A senha deve ter pelo menos 6 caracteres');
            setLoading(false);
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const submitData = {
                username: formData.username,
                email: formData.email,
                profile: formData.profile
            };

            if (!isEditing) {
                submitData.password = formData.password;
            }

            const url = isEditing 
                ? `http://localhost:5000/api/admin/trainers/${trainerId}`
                : 'http://localhost:5000/api/admin/trainers';
            
            const method = isEditing ? 'put' : 'post';
            
            await axios[method](url, submitData, {
                headers: { Authorization: `Bearer ${token}` }
            });

            history.push('/admin');
        } catch (error) {
            console.error('Error saving trainer:', error);
            setError(error.response?.data?.message || 'Erro ao salvar personal');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="trainer-form-page">
            <div className="form-header">
                <h1>{isEditing ? 'Editar Personal Trainer' : 'Adicionar Personal Trainer'}</h1>
                <button 
                    onClick={() => history.push('/admin')}
                    className="btn btn-secondary"
                >
                    Voltar
                </button>
            </div>

            <form onSubmit={handleSubmit} className="trainer-form">
                {error && <div className="error-message">{error}</div>}

                <div className="form-section">
                    <h3>Informações de Login</h3>
                    
                    <div className="form-group">
                        <label htmlFor="username">Nome de usuário *</label>
                        <input
                            type="text"
                            id="username"
                            name="username"
                            value={formData.username}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="email">Email *</label>
                        <input
                            type="email"
                            id="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    {!isEditing && (
                        <>
                            <div className="form-group">
                                <label htmlFor="password">Senha *</label>
                                <input
                                    type="password"
                                    id="password"
                                    name="password"
                                    value={formData.password}
                                    onChange={handleChange}
                                    required={!isEditing}
                                    minLength="6"
                                />
                            </div>

                            <div className="form-group">
                                <label htmlFor="confirmPassword">Confirmar Senha *</label>
                                <input
                                    type="password"
                                    id="confirmPassword"
                                    name="confirmPassword"
                                    value={formData.confirmPassword}
                                    onChange={handleChange}
                                    required={!isEditing}
                                />
                            </div>
                        </>
                    )}
                </div>

                <div className="form-section">
                    <h3>Informações Pessoais</h3>
                    
                    <div className="form-row">
                        <div className="form-group">
                            <label htmlFor="profile.firstName">Nome</label>
                            <input
                                type="text"
                                id="profile.firstName"
                                name="profile.firstName"
                                value={formData.profile.firstName}
                                onChange={handleChange}
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="profile.lastName">Sobrenome</label>
                            <input
                                type="text"
                                id="profile.lastName"
                                name="profile.lastName"
                                value={formData.profile.lastName}
                                onChange={handleChange}
                            />
                        </div>
                    </div>

                    <div className="form-group">
                        <label htmlFor="profile.phone">Telefone</label>
                        <input
                            type="text"
                            id="profile.phone"
                            name="profile.phone"
                            value={formData.profile.phone}
                            onChange={handleChange}
                        />
                    </div>
                </div>

                <div className="form-section">
                    <h3>Informações Profissionais</h3>
                    
                    <div className="form-group">
                        <label>Especialidades</label>
                        <div className="specialties-grid">
                            {specialtyOptions.map(specialty => (
                                <label key={specialty} className="checkbox-label">
                                    <input
                                        type="checkbox"
                                        checked={formData.profile.specialties.includes(specialty)}
                                        onChange={() => handleSpecialtyChange(specialty)}
                                    />
                                    {specialty}
                                </label>
                            ))}
                        </div>
                    </div>

                    <div className="form-group">
                        <label htmlFor="profile.experience">Experiência</label>
                        <textarea
                            id="profile.experience"
                            name="profile.experience"
                            value={formData.profile.experience}
                            onChange={handleChange}
                            rows="3"
                            placeholder="Descreva a experiência profissional..."
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="profile.certification">Certificações</label>
                        <textarea
                            id="profile.certification"
                            name="profile.certification"
                            value={formData.profile.certification}
                            onChange={handleChange}
                            rows="3"
                            placeholder="Liste as certificações..."
                        />
                    </div>
                </div>

                <div className="form-actions">
                    <button 
                        type="button" 
                        onClick={() => history.push('/admin')}
                        className="btn btn-secondary"
                    >
                        Cancelar
                    </button>
                    <button 
                        type="submit" 
                        disabled={loading}
                        className="btn btn-primary"
                    >
                        {loading ? 'Salvando...' : (isEditing ? 'Atualizar' : 'Criar Personal')}
                    </button>
                </div>
            </form>
        </div>
    );
};

export default TrainerForm;
