import React, { useContext } from 'react';
import { AuthContext } from '../contexts/AuthContext';
import AdminDashboard from './AdminDashboard';
import Dashboard from '../components/dashboard/Dashboard';
import Stats from '../components/dashboard/Stats';

const DashboardPage = () => {
    const { user } = useContext(AuthContext);

    if (!user) {
        return <div>Carregando...</div>;
    }

    // Se for admin, mostrar dashboard de admin
    if (user.role === 'admin') {
        return <AdminDashboard />;
    }

    // Se for cliente (role 'user'), mostrar dashboard de cliente
    if (user.role === 'user') {
        return (
            <div className="client-dashboard">
                <h1>Dashboard - Cliente</h1>
                <div className="welcome-message">
                    <h2>Bem-vindo, {user.username}!</h2>
                    <p>Aqui você pode acompanhar seus treinos e progressos.</p>
                </div>
                <div className="client-stats">
                    <div className="stat-card">
                        <h3>Meus Treinos</h3>
                        <p>Em breve você poderá visualizar seus planos de treino aqui.</p>
                    </div>
                    <div className="stat-card">
                        <h3>Métricas</h3>
                        <p>Acompanhe seu progresso e evolução.</p>
                    </div>
                    <div className="stat-card">
                        <h3>Meu Treinador</h3>
                        <p>Informações sobre seu personal trainer.</p>
                    </div>
                </div>
            </div>
        );
    }

    // Dashboard para personal trainers (role 'trainer')
    return (
        <div className="trainer-dashboard">
            <h1>Dashboard - Personal Trainer</h1>
            <div className="welcome-message">
                <h2>Bem-vindo, {user.username}!</h2>
                <p>Gerencie seus clientes e planos de treino.</p>
            </div>
            <Dashboard />
            <Stats />
        </div>
    );
};

export default DashboardPage;
