const BackIcon = ({ size = 20, color = '#6b7280' }) => (
    <svg width={size} height={size} viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M20,11V13H8L13.5,18.5L12.08,19.92L4.16,12L12.08,4.08L13.5,5.5L8,11H20Z" fill={color}/>
    </svg>
);

const ClientDetailPage = () => {
    const { clientId } = useParams();
    const history = useHistory();
    const { clients } = useContext(ClientContext);
    const [client, setClient] = useState(null);
    const [metrics, setMetrics] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchClientDetails = async () => {
            try {
                setLoading(true);
                console.log('üîç Buscando detalhes do cliente:', clientId);

                // Buscar dados do cliente
                const clientData = await clientService.getClientById(clientId);
                console.log('‚úÖ Cliente encontrado:', clientData);
                setClient(clientData);

                // Buscar m√©tricas do cliente
                try {
                    const metricsData = await getMetricsByClientId(clientId);
                    setMetrics(metricsData || []);
                } catch (metricsError) {
                    console.error('‚ùå Erro ao buscar m√©tricas:', metricsError);
                    setMetrics([]);
                }

            } catch (err) {
                console.error('‚ùå Erro ao buscar cliente:', err);
                setError('Falha ao carregar detalhes do cliente');
            } finally {
                setLoading(false);
            }
        };

        fetchClientDetails();
    }, [clientId]);

    const getLatestMetrics = () => {
        if (!metrics || metrics.length === 0) return null;
        return metrics.sort((a, b) => new Date(b.date) - new Date(a.date))[0];
    };

    if (loading) {
        return (
            <div className="page-container">
                <div className="loading-container">
                    <div className="loading-spinner"></div>
                    <p>Carregando detalhes do cliente...</p>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="page-container">
                <div className="error-message">
                    <h3>Erro ao carregar cliente</h3>
                    <p>{error}</p>
                    <button onClick={() => history.goBack()} className="btn btn-secondary">
                        Voltar
                    </button>
                </div>
            </div>
        );
    }

    if (!client) {
        return (
            <div className="page-container">
                <div className="empty-state">
                    <h3>Cliente n√£o encontrado</h3>
                    <p>O cliente solicitado n√£o foi encontrado ou voc√™ n√£o tem permiss√£o para acess√°-lo.</p>
                    <Link to="/clients" className="btn btn-primary">
                        Voltar para Clientes
                    </Link>
                </div>
            </div>
        );
    }

    const latestMetrics = getLatestMetrics();

    return (
        <div className="page-container">
            {/* Header */}
            <div className="page-header">
                <div className="header-content">
                    <div className="header-navigation">
                        <button 
                            onClick={() => history.goBack()} 
                            className="btn btn-secondary btn-small"
                        >
                            <BackIcon size={16} />
                            Voltar
                        </button>
                    </div>
                    <div className="header-info">
                        <UserIcon size={32} />
                        <div>
                            <h1>Perfil do Cliente</h1>
                            <p>Detalhes, m√©tricas e hist√≥rico de treinos</p>
                        </div>
                    </div>
                </div>
            </div>

            <div className="page-content">
                {/* Informa√ß√µes do Cliente */}
                <div className="client-profile-section">
                    <div className="profile-header">
                        <div className="client-avatar">
                            <UserIcon size={48} color="#6366f1" />
                        </div>
                        <div className="client-basic-info">
                            <h2>{client.name}</h2>
                            <p className="client-email">{client.email}</p>
                            <p className="client-phone">{client.phone}</p>
                            <div className="client-status">
                                <span className={`status ${client.isActive !== false ? 'active' : 'inactive'}`}>
                                    {client.isActive !== false ? 'Ativo' : 'Inativo'}
                                </span>
                                <span className="join-date">
                                    Cliente desde {new Date(client.createdAt).toLocaleDateString('pt-BR')}
                                </span>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Estat√≠sticas R√°pidas */}
                <div className="client-stats-section">
                    <div className="stats-grid">
                        <div className="stat-card">
                            <div className="stat-header">
                                <WorkoutIcon size={24} />
                                <h3>Planos de Treino</h3>
                            </div>
                            <div className="stat-value">
                                {client.workoutPlans ? client.workoutPlans.length : 0}
                            </div>
                            <div className="stat-subtitle">Treinos atribu√≠dos</div>
                        </div>

                        <div className="stat-card">
                            <div className="stat-header">
                                <MetricsIcon size={24} />
                                <h3>Registros</h3>
                            </div>
                            <div className="stat-value">{metrics.length}</div>
                            <div className="stat-subtitle">M√©tricas corporais</div>
                        </div>

                        {latestMetrics && (
                            <>
                                <div className="stat-card">
                                    <div className="stat-header">
                                        <h3>Peso Atual</h3>
                                    </div>
                                    <div className="stat-value">{latestMetrics.weight}kg</div>
                                    <div className="stat-subtitle">
                                        √öltima medi√ß√£o: {new Date(latestMetrics.date).toLocaleDateString('pt-BR')}
                                    </div>
                                </div>

                                <div className="stat-card">
                                    <div className="stat-header">
                                        <h3>Altura</h3>
                                    </div>
                                    <div className="stat-value">{latestMetrics.height}cm</div>
                                    <div className="stat-subtitle">
                                        IMC: {(latestMetrics.weight / Math.pow(latestMetrics.height/100, 2)).toFixed(1)}
                                    </div>
                                </div>
                            </>
                        )}
                    </div>
                </div>

                {/* Se√ß√µes de Hist√≥rico */}
                <div className="client-history-section">
                    <div className="history-tabs">
                        <div className="tab-content">
                            {/* Hist√≥rico de M√©tricas */}
                            <div className="metrics-section">
                                <div className="section-header">
                                    <div className="section-title">
                                        <MetricsIcon size={20} />
                                        <h3>Hist√≥rico de M√©tricas</h3>
                                    </div>
                                </div>
                                <div className="section-content">
                                    <MetricsHistory clientId={clientId} />
                                </div>
                            </div>

                            {/* Hist√≥rico de Treinos */}
                            <div className="workouts-section">
                                <div className="section-header">
                                    <div className="section-title">
                                        <WorkoutIcon size={20} />
                                        <h3>Hist√≥rico de Treinos</h3>
                                    </div>
                                </div>
                                <div className="section-content">
                                    <WorkoutHistory clientId={clientId} />
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ClientDetailPage;
