import React, { useContext, useEffect, useState } from 'react';
import { useParams, Link, useHistory } from 'react-router-dom';
import { ClientContext } from '../contexts/ClientContext';
import { AuthContext } from '../contexts/AuthContext';
import * as clientService from '../services/client.service';
import MetricsHistory from '../components/metrics/MetricsHistory';
import WorkoutHistory from '../components/workouts/WorkoutHistory';

const ClientDetailPage = () => {
    const { clientId } = useParams();
    const { clients } = useContext(ClientContext);
    const { user } = useContext(AuthContext);
    const history = useHistory();
    const [client, setClient] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchClientDetails = async () => {
            try {
                console.log('üîç Buscando detalhes do cliente:', clientId);
                const response = await clientService.getClientById(clientId);
                console.log('‚úÖ Cliente encontrado:', response);
                setClient(response);
            } catch (err) {
                console.error('‚ùå Erro ao buscar cliente:', err);
                setError('Erro ao carregar detalhes do cliente');
            } finally {
                setLoading(false);
            }
        };

        if (clientId) {
            fetchClientDetails();
        }
    }, [clientId]);

    const handleGoBack = () => {
        if (user?.role === 'trainer') {
            history.push('/trainer/dashboard');
        } else {
            history.push('/clients');
        }
    };

    if (loading) {
        return (
            <div className="loading-container">
                <div className="loading-spinner"></div>
                <p>Carregando detalhes do cliente...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div className="error-container">
                <div className="error-icon">‚ö†Ô∏è</div>
                <h2>Erro</h2>
                <p>{error}</p>
                <button onClick={handleGoBack} className="btn btn-primary">
                    Voltar
                </button>
            </div>
        );
    }

    if (!client) {
        return (
            <div className="empty-state">
                <div className="empty-icon">üë§</div>
                <h2>Cliente n√£o encontrado</h2>
                <p>O cliente solicitado n√£o foi encontrado ou voc√™ n√£o tem permiss√£o para visualiz√°-lo.</p>
                <button onClick={handleGoBack} className="btn btn-primary">
                    Voltar
                </button>
            </div>
        );
    }

    return (
        <div className="client-detail-page">
            {/* Header */}
            <div className="page-header">
                <div className="header-content">
                    <button onClick={handleGoBack} className="back-button">
                        ‚Üê Voltar
                    </button>
                    <div className="header-info">
                        <h1>Detalhes do Cliente</h1>
                        <p className="subtitle">Informa√ß√µes completas e hist√≥rico</p>
                    </div>
                </div>
            </div>

            {/* Client Information */}
            <div className="client-info-section">
                <div className="client-header">
                    <div className="client-avatar-large">
                        {client.name?.charAt(0).toUpperCase() || '?'}
                    </div>
                    <div className="client-basic-info">
                        <h2>{client.name || 'Nome n√£o informado'}</h2>
                        <p className="client-email">{client.email || 'Email n√£o informado'}</p>
                        <p className="client-phone">{client.phone || 'Telefone n√£o informado'}</p>
                        <span className={`status-badge ${client.isActive !== false ? 'active' : 'inactive'}`}>
                            {client.isActive !== false ? 'Ativo' : 'Inativo'}
                        </span>
                    </div>
                    <div className="client-actions">
                        <Link 
                            to={`/workout-plan/new?client=${client._id}`} 
                            className="btn btn-primary"
                        >
                            <span className="icon">üí™</span>
                            Novo Treino
                        </Link>
                        <Link 
                            to={`/clients/${client._id}/edit`} 
                            className="btn btn-secondary"
                        >
                            <span className="icon">‚úèÔ∏è</span>
                            Editar
                        </Link>
                    </div>
                </div>

                {/* Physical Stats */}
                <div className="physical-stats">
                    <h3>Informa√ß√µes F√≠sicas Atuais</h3>
                    <div className="stats-grid">
                        <div className="stat-item">
                            <span className="stat-label">Peso</span>
                            <span className="stat-value">
                                {client.metrics && client.metrics.length > 0 
                                    ? `${client.metrics[client.metrics.length - 1].weight.toFixed(1)} kg`
                                    : 'N√£o informado'
                                }
                            </span>
                        </div>
                        <div className="stat-item">
                            <span className="stat-label">Altura</span>
                            <span className="stat-value">
                                {client.metrics && client.metrics.length > 0 
                                    ? `${(client.metrics[client.metrics.length - 1].height * 100).toFixed(0)} cm`
                                    : 'N√£o informado'
                                }
                            </span>
                        </div>
                        <div className="stat-item">
                            <span className="stat-label">Gordura Corporal</span>
                            <span className="stat-value">
                                {client.metrics && client.metrics.length > 0 
                                    ? `${client.metrics[client.metrics.length - 1].bodyFatPercentage.toFixed(1)}%`
                                    : 'N√£o informado'
                                }
                            </span>
                        </div>
                        <div className="stat-item">
                            <span className="stat-label">Massa Muscular</span>
                            <span className="stat-value">
                                {client.metrics && client.metrics.length > 0 
                                    ? `${client.metrics[client.metrics.length - 1].muscleMassPercentage.toFixed(1)}%`
                                    : 'N√£o informado'
                                }
                            </span>
                        </div>
                        {client.metrics && client.metrics.length > 0 && (
                            <div className="stat-item">
                                <span className="stat-label">IMC</span>
                                <span className="stat-value">
                                    {(client.metrics[client.metrics.length - 1].weight / 
                                      (client.metrics[client.metrics.length - 1].height ** 2)).toFixed(1)}
                                </span>
                            </div>
                        )}
                        <div className="stat-item">
                            <span className="stat-label">√öltima Atualiza√ß√£o</span>
                            <span className="stat-value">
                                {client.metrics && client.metrics.length > 0 
                                    ? new Date(client.metrics[client.metrics.length - 1].date).toLocaleDateString('pt-BR')
                                    : 'Sem dados'
                                }
                            </span>
                        </div>
                    </div>
                </div>

                {/* Goals and Notes */}
                {(client.goals || client.notes) && (
                    <div className="additional-info">
                        {client.goals && (
                            <div className="info-section">
                                <h4>Objetivos</h4>
                                <p>{client.goals}</p>
                            </div>
                        )}
                        {client.notes && (
                            <div className="info-section">
                                <h4>Observa√ß√µes</h4>
                                <p>{client.notes}</p>
                            </div>
                        )}
                    </div>
                )}
            </div>

            {/* Evolution Dashboard */}
            <div className="evolution-dashboard">
                <h3>Dashboard de Evolu√ß√£o</h3>

                {client.metrics && client.metrics.length > 1 ? (
                    <>
                        <div className="evolution-grid">
                            {/* Weight Evolution */}
                            <div className="evolution-card">
                                <div className="evolution-header">
                                    <h4>üìà Evolu√ß√£o do Peso</h4>
                                    <div className="evolution-trend">
                                        {(() => {
                                            const firstWeight = client.metrics[0].weight;
                                            const lastWeight = client.metrics[client.metrics.length - 1].weight;
                                            const diff = lastWeight - firstWeight;
                                            return (
                                                <span className={`trend ${diff > 0 ? 'up' : diff < 0 ? 'down' : 'stable'}`}>
                                                    {diff > 0 ? '‚ÜóÔ∏è' : diff < 0 ? '‚ÜòÔ∏è' : '‚û°Ô∏è'} 
                                                    {Math.abs(diff).toFixed(1)}kg
                                                </span>
                                            );
                                        })()}
                                    </div>
                                </div>
                                <div className="evolution-chart">
                                    <div className="mini-chart">
                                        {client.metrics.slice(-6).map((metric, index) => (
                                            <div key={index} className="chart-bar">
                                                <div 
                                                    className="bar weight-bar" 
                                                    style={{ 
                                                        height: `${(metric.weight / Math.max(...client.metrics.map(m => m.weight))) * 100}%` 
                                                    }}
                                                ></div>
                                                <span className="bar-label">
                                                    {new Date(metric.date).toLocaleDateString('pt-BR', { month: 'short' })}
                                                </span>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            </div>

                            {/* Body Fat Evolution */}
                            <div className="evolution-card">
                                <div className="evolution-header">
                                    <h4>üìä Evolu√ß√£o da Gordura Corporal</h4>
                                    <div className="evolution-trend">
                                        {(() => {
                                            const firstFat = client.metrics[0].bodyFatPercentage;
                                            const lastFat = client.metrics[client.metrics.length - 1].bodyFatPercentage;
                                            const diff = lastFat - firstFat;
                                            return (
                                                <span className={`trend ${diff > 0 ? 'up-bad' : diff < 0 ? 'down-good' : 'stable'}`}>
                                                    {diff > 0 ? '‚ÜóÔ∏è' : diff < 0 ? '‚ÜòÔ∏è' : '‚û°Ô∏è'} 
                                                    {Math.abs(diff).toFixed(1)}%
                                                </span>
                                            );
                                        })()}
                                    </div>
                                </div>
                                <div className="evolution-chart">
                                    <div className="mini-chart">
                                        {client.metrics.slice(-6).map((metric, index) => (
                                            <div key={index} className="chart-bar">
                                                <div 
                                                    className="bar fat-bar" 
                                                    style={{ 
                                                        height: `${(metric.bodyFatPercentage / Math.max(...client.metrics.map(m => m.bodyFatPercentage))) * 100}%` 
                                                    }}
                                                ></div>
                                                <span className="bar-label">
                                                    {new Date(metric.date).toLocaleDateString('pt-BR', { month: 'short' })}
                                                </span>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            </div>

                            {/* Muscle Mass Evolution */}
                            <div className="evolution-card">
                                <div className="evolution-header">
                                    <h4>üí™ Evolu√ß√£o da Massa Muscular</h4>
                                    <div className="evolution-trend">
                                        {(() => {
                                            const firstMuscle = client.metrics[0].muscleMassPercentage;
                                            const lastMuscle = client.metrics[client.metrics.length - 1].muscleMassPercentage;
                                            const diff = lastMuscle - firstMuscle;
                                            return (
                                                <span className={`trend ${diff > 0 ? 'up-good' : diff < 0 ? 'down-bad' : 'stable'}`}>
                                                    {diff > 0 ? '‚ÜóÔ∏è' : diff < 0 ? '‚ÜòÔ∏è' : '‚û°Ô∏è'} 
                                                    {Math.abs(diff).toFixed(1)}%
                                                </span>
                                            );
                                        })()}
                                    </div>
                                </div>
                                <div className="evolution-chart">
                                    <div className="mini-chart">
