import React, { useEffect, useState } from 'react';
import * as workoutService from '../../services/workout.service';

const WorkoutHistory = ({ clientId }) => {
    const [workouts, setWorkouts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedWorkout, setSelectedWorkout] = useState(null);

    useEffect(() => {
        const fetchWorkoutHistory = async () => {
            if (!clientId) {
                setError('ID do cliente não fornecido');
                setLoading(false);
                return;
            }

            try {
                console.log('🏋️ Buscando histórico de treinos para cliente:', clientId);
                const response = await workoutService.getWorkoutsByClientId(clientId);
                console.log('🏋️ Treinos encontrados:', response);
                setWorkouts(Array.isArray(response) ? response : response.data || []);
            } catch (err) {
                console.error('❌ Erro ao buscar histórico de treinos:', err);
                setError('Erro ao carregar histórico de treinos');
            } finally {
                setLoading(false);
            }
        };

        fetchWorkoutHistory();
    }, [clientId]);

    const handleWorkoutClick = (workout) => {
        setSelectedWorkout(workout);
    };

    const closeModal = () => {
        setSelectedWorkout(null);
    };

    if (loading) {
        return (
            <div className="loading-container">
                <div className="loading-spinner"></div>
                <p>Carregando histórico de treinos...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div className="error-container">
                <span className="error-icon">⚠️</span>
                <p>{error}</p>
            </div>
        );
    }

    if (!workouts || workouts.length === 0) {
        return (
            <div className="empty-state">
                <div className="empty-icon">🏋️</div>
                <h4>Nenhum treino encontrado</h4>
                <p>Este cliente ainda não possui treinos cadastrados.</p>
            </div>
        );
    }

    return (
        <div className="workout-history">
            <div className="workouts-list">
                {workouts.map((workout, index) => (
                    <div 
                        key={workout._id || index} 
                        className="workout-item clickable"
                        onClick={() => handleWorkoutClick(workout)}
                    >
                        <div className="workout-header">
                            <h4>{workout.name || workout.title || `Treino ${index + 1}`}</h4>
                            <span className="workout-date">
                                {workout.createdAt 
                                    ? new Date(workout.createdAt).toLocaleDateString('pt-BR')
                                    : 'Data não informada'
                                }
                            </span>
                        </div>
                        {workout.description && (
                            <p className="workout-description">{workout.description}</p>
                        )}
                        <div className="workout-meta">
                            {workout.exercises && workout.exercises.length > 0 && (
                                <span className="exercises-count">
                                    {workout.exercises.length} exercício(s)
                                </span>
                            )}
                            {workout.status && (
                                <span className={`status-badge ${workout.status.toLowerCase()}`}>
                                    {workout.status === 'active' ? 'Ativo' : 
                                     workout.status === 'completed' ? 'Concluído' : workout.status}
                                </span>
                            )}
                            <span className="click-hint">👁️ Clique para ver detalhes</span>
                        </div>
                    </div>
                ))}
            </div>

            {/* Modal de Detalhes do Treino */}
            {selectedWorkout && (
                <div className="workout-modal-overlay" onClick={closeModal}>
                    <div className="workout-modal" onClick={(e) => e.stopPropagation()}>
                        <div className="workout-modal-header">
                            <h2>{selectedWorkout.name || selectedWorkout.title || 'Detalhes do Treino'}</h2>
                            <button className="close-button" onClick={closeModal}>×</button>
                        </div>

                        <div className="workout-modal-content">
                            <div className="workout-info">
                                <div className="info-row">
                                    <strong>Data:</strong> 
                                    <span>
                                        {selectedWorkout.createdAt 
                                            ? new Date(selectedWorkout.createdAt).toLocaleDateString('pt-BR')
                                            : 'Não informada'
                                        }
                                    </span>
                                </div>
                                {selectedWorkout.description && (
                                    <div className="info-row">
                                        <strong>Descrição:</strong>
                                        <span>{selectedWorkout.description}</span>
                                    </div>
                                )}
                                {selectedWorkout.status && (
                                    <div className="info-row">
                                        <strong>Status:</strong>
                                        <span className={`status-badge ${selectedWorkout.status.toLowerCase()}`}>
                                            {selectedWorkout.status === 'active' ? 'Ativo' : 
                                             selectedWorkout.status === 'completed' ? 'Concluído' : 
                                             selectedWorkout.status === 'pending' ? 'Pendente' :
                                             selectedWorkout.status}
                                        </span>
                                    </div>
                                )}
                                {selectedWorkout.difficulty && (
                                    <div className="info-row">
                                        <strong>Dificuldade:</strong>
                                        <span className={`difficulty-badge ${selectedWorkout.difficulty.toLowerCase()}`}>
                                            {selectedWorkout.difficulty === 'easy' ? 'Fácil' :
                                             selectedWorkout.difficulty === 'medium' ? 'Médio' :
                                             selectedWorkout.difficulty === 'hard' ? 'Difícil' :
                                             selectedWorkout.difficulty}
                                        </span>
                                    </div>
                                )}
                                {selectedWorkout.exercises && selectedWorkout.exercises.length > 0 && (
                                    <div className="info-row">
                                        <strong>Total de Exercícios:</strong>
                                        <span>{selectedWorkout.exercises.length}</span>
                                    </div>
                                )}
                                {selectedWorkout.updatedAt && selectedWorkout.updatedAt !== selectedWorkout.createdAt && (
                                    <div className="info-row">
                                        <strong>Última Atualização:</strong>
                                        <span>
                                            {new Date(selectedWorkout.updatedAt).toLocaleString('pt-BR')}
                                        </span>
                                    </div>
                                )}
                            </div>

                            {selectedWorkout.exercises && selectedWorkout.exercises.length > 0 && (
                                <div className="exercises-section">
                                    <h3>Exercícios ({selectedWorkout.exercises.length})</h3>
                                    <div className="exercises-list">
                                        {selectedWorkout.exercises.map((exercise, index) => (
                                            <div key={index} className="exercise-card">
                                                <div className="exercise-header">
                                                    <h4>{exercise.name || `Exercício ${index + 1}`}</h4>
                                                </div>
                                                <div className="exercise-details">                                    <div className="exercise-specs">
                                        {exercise.sets && (
                                            <div className="spec-item">
                                                <span className="spec-label">Séries:</span>
                                                <span className="spec-value">{exercise.sets}</span>
                                            </div>
                                        )}
                                        {exercise.reps && (
                                            <div className="spec-item">
                                                <span className="spec-label">Repetições:</span>
                                                <span className="spec-value">{exercise.reps}</span>
                                            </div>
                                        )}
                                        {exercise.weight && (
                                            <div className="spec-item">
                                                <span className="spec-label">Carga:</span>
                                                <span className="spec-value">{exercise.weight}</span>
                                            </div>
                                        )}
                                        {exercise.rest && (
                                            <div className="spec-item">
                                                <span className="spec-label">Descanso:</span>
                                                <span className="spec-value">{exercise.rest}</span>
                                            </div>
                                        )}
                                        {exercise.duration && (
                                            <div className="spec-item">
                                                <span className="spec-label">Duração:</span>
                                                <span className="spec-value">{exercise.duration}</span>
                                            </div>
                                        )}
                                        {exercise.tempo && (
                                            <div className="spec-item">
                                                <span className="spec-label">Tempo:</span>
                                                <span className="spec-value">{exercise.tempo}</span>
                                            </div>
                                        )}
                                        {exercise.distance && (
                                            <div className="spec-item">
                                                <span className="spec-label">Distância:</span>
                                                <span className="spec-value">{exercise.distance}</span>
                                            </div>
                                        )}
                                    </div>
                                                    {exercise.notes && (
                                                        <div className="exercise-notes">
                                                            <strong>Observações:</strong> {exercise.notes}
                                                        </div>
                                                    )}
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            )}

                            {selectedWorkout.notes && (
                                <div className="workout-notes">
                                    <h3>Observações do Treino</h3>
                                    <p>{selectedWorkout.notes}</p>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default WorkoutHistory;
