import React, { useEffect, useState } from 'react';
import { fetchMetrics } from '../../services/metrics.service';

const MetricsHistory = ({ clientId }) => {
    const [metrics, setMetrics] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const getMetrics = async () => {
            try {
                const data = await fetchMetrics(clientId);
                setMetrics(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        getMetrics();
    }, [clientId]);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div>
            <h2>Histórico de Métricas</h2>
            <table>
                <thead>
                    <tr>
                        <th>Data</th>
                        <th>Peso</th>
                        <th>Altura</th>
                        <th>Percentual de Gordura</th>
                        <th>Percentual de Massa Muscular</th>
                    </tr>
                </thead>
                <tbody>
                    {metrics.map((metric) => (
                        <tr key={metric.id}>
                            <td>{new Date(metric.date).toLocaleDateString()}</td>
                            <td>{metric.weight}</td>
                            <td>{metric.height}</td>
                            <td>{metric.bodyFatPercentage}</td>
                            <td>{metric.muscleMassPercentage}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default MetricsHistory;
