import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../../contexts/AuthContext';

const Navigation = () => {
    const { user, isAuthenticated } = useContext(AuthContext) || { user: null, isAuthenticated: false };
    const isAdmin = user && user.role === 'admin';
    const isTrainer = user && user.role === 'trainer';
    const isClient = user && user.role === 'user';

    return (
        <nav className="navigation">
            <ul>
                {isAuthenticated ? (
                    // Links for authenticated users based on role
                    <>
                        <li>
                            <Link to={isTrainer ? "/trainer/dashboard" : "/"}>Painel</Link>
                        </li>

                        {/* Admin Links */}
                        {isAdmin && (
                            <>
                                <li>
                                    <Link to="/admin/trainers">Gerenciar Treinadores</Link>
                                </li>
                                <li>
                                    <Link to="/admin/clients">Todos os Clientes</Link>
                                </li>
                            </>
                        )}

                        {/* Trainer Links */}
                        {isTrainer && (
                            <>
                                <li>
                                    <Link to="/trainer/dashboard">Painel do Treinador</Link>
                                </li>
                                <li>
                                    <Link to="/clients">Meus Clientes</Link>
                                </li>
                                <li>
                                    <Link to="/workout-plan">Planos de Treino</Link>
                                </li>
                            </>
                        )}

                        {/* Client Links */}
                        {isClient && (
                            <>
                                <li>
                                    <Link to="/my-workouts">Meus Treinos</Link>
                                </li>
                                <li>
                                    <Link to="/my-progress">Meu Progresso</Link>
                                </li>
                                <li>
                                    <Link to="/my-trainer">Meu Treinador</Link>
                                </li>
                            </>
                        )}
                    </>
                ) : (
                    // Links for non-authenticated users
                    <>
                        <li>
                            <Link to="/login">Login</Link>
                        </li>
                        <li>
                            <Link to="/register">Cadastrar</Link>
                        </li>
                    </>
                )}
            </ul>
        </nav>
    );
};

export default Navigation;
