import React, { useEffect, useState, useContext } from 'react';
import { ClientContext } from '../contexts/ClientContext';
import { AuthContext } from '../contexts/AuthContext';
import WorkoutPlan from '../components/workouts/WorkoutPlan';

const WorkoutPlanPage = () => {
    const { clients } = useContext(ClientContext);
    const { user } = useContext(AuthContext);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Simular carregamento rápido
        const timer = setTimeout(() => {
            setLoading(false);
        }, 500);

        return () => clearTimeout(timer);
    }, []);

    if (loading) {
        return (
            <div className="workout-plans-container">
                <div className="loading-container">
                    <div className="loading-spinner"></div>
                    <p>Carregando...</p>
                </div>
            </div>
        );
    }

    // Para trainer, sempre mostrar o componente (ele mostrará uma mensagem se não houver treinos)
    // Para cliente, sempre mostrar também (o componente lidará com a lógica interna)
    return <WorkoutPlan />;
};

export default WorkoutPlanPage;
