import React from 'react';

const ClientCard = ({ client }) => {
    return (
        <div className="client-card">
            <h3>{client.name}</h3>
            <p><strong>Peso:</strong> {client.weight} kg</p>
            <p><strong>Altura:</strong> {client.height} cm</p>
            <p><strong>Percentual de Gordura:</strong> {client.bodyFatPercentage}%</p>
            <p><strong>Percentual de Massa Muscular:</strong> {client.muscleMassPercentage}%</p>
            <p><strong>Histórico de Evolução:</strong></p>
            <ul>
                {client.progressHistory.map((entry, index) => (
                    <li key={index}>{entry.date}: {entry.note}</li>
                ))}
            </ul>
        </div>
    );
};

export default ClientCard;
