import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TrainerForm = ({ trainer, onClose }) => {
    const isEditing = !!trainer;
    const [editMode, setEditMode] = useState(!isEditing); // Para novos: true, para existentes: false (visualização primeiro)

    const [formData, setFormData] = useState({
        username: '',
        email: '',
        password: '',
        confirmPassword: '',
        profile: {
            firstName: '',
            lastName: '',
            phone: '',
            specialties: [],
            experience: '',
            certification: ''
        }
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const specialtyOptions = [
        'Musculação',
        'Cardio',
        'Funcional',
        'Pilates',
        'Yoga',
        'CrossFit',
        'Natação',
        'Corrida',
        'Fisioterapia',
        'Nutrição Esportiva'
    ];

    useEffect(() => {
        if (isEditing && trainer) {
            setFormData({
                username: trainer.username || '',
                email: trainer.email || '',
                password: '',
                confirmPassword: '',
                profile: {
                    firstName: trainer.profile?.firstName || '',
                    lastName: trainer.profile?.lastName || '',
                    phone: trainer.profile?.phone || '',
                    specialties: trainer.profile?.specialties || [],
                    experience: trainer.profile?.experience || '',
                    certification: trainer.profile?.certification || ''
                }
            });
        }
    }, [trainer, isEditing, editMode]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        
        if (name.startsWith('profile.')) {
            const profileField = name.replace('profile.', '');
            setFormData(prev => ({
                ...prev,
                profile: {
                    ...prev.profile,
                    [profileField]: value
                }
            }));
        } else {
            setFormData(prev => ({
                ...prev,
                [name]: value
            }));
        }
    };

    const handleSpecialtyChange = (specialty) => {
        setFormData(prev => ({
            ...prev,
            profile: {
                ...prev.profile,
                specialties: prev.profile.specialties.includes(specialty)
                    ? prev.profile.specialties.filter(s => s !== specialty)
                    : [...prev.profile.specialties, specialty]
            }
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        // Validations
        if (!isEditing && formData.password !== formData.confirmPassword) {
            setError('As senhas não coincidem');
            setLoading(false);
            return;
        }

        if (!isEditing && formData.password.length < 6) {
            setError('A senha deve ter pelo menos 6 caracteres');
            setLoading(false);
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const submitData = {
                username: formData.username,
                email: formData.email,
                profile: formData.profile
            };

            // Para edição, preservar o status ativo atual
            if (isEditing) {
                submitData.isActive = trainer.isActive;
            }

            if (!isEditing) {
                submitData.password = formData.password;
            }

            const url = isEditing 
                ? `http://localhost:5000/api/admin/trainers/${trainer._id}`
                : 'http://localhost:5000/api/admin/trainers';
            
            const method = isEditing ? 'put' : 'post';
            
            await axios[method](url, submitData, {
                headers: { Authorization: `Bearer ${token}` }
            });

            setEditMode(false); // Voltar para modo visualização
            onClose(); // Fechar o modal e atualizar a lista
        } catch (error) {
            console.error('Error saving trainer:', error);
            setError(error.response?.data?.message || 'Erro ao salvar personal');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="trainer-form-modal">
            <div className="form-header">
                <h2>{isEditing ? 'Perfil do Personal Trainer' : 'Adicionar Personal Trainer'}</h2>
                <button 
                    onClick={onClose}
                    className="btn btn-secondary close-btn"
                    type="button"
                >
                    ✕
                </button>
            </div>

            {isEditing && !editMode ? (
                // Modo visualização para treinadores existentes
                <div className="trainer-view" style={{background: '#f0f8ff', border: '2px solid blue'}}>
                    <p>DEBUG: Modo visualização ativado - isEditing: {isEditing.toString()}, editMode: {editMode.toString()}</p>
                    <div className="view-section">
                        <div className="section-header">
                            <h3>Informações de Login</h3>
                            <button 
                                className="btn btn-small btn-primary"
                                onClick={() => setEditMode(true)}
                            >
                                Editar
                            </button>
                        </div>
                        <div className="info-grid">
                            <div className="info-item">
                                <label>Nome de usuário:</label>
                                <span>{formData.username}</span>
                            </div>
                            <div className="info-item">
                                <label>Email:</label>
                                <span>{formData.email}</span>
                            </div>
                        </div>
                    </div>

                    <div className="view-section">
                        <h3>Informações Pessoais</h3>
                        <div className="info-grid">
                            <div className="info-item">
                                <label>Nome:</label>
                                <span>{formData.profile.firstName} {formData.profile.lastName}</span>
                            </div>
                            <div className="info-item">
                                <label>Telefone:</label>
                                <span>{formData.profile.phone || 'Não informado'}</span>
                            </div>
                            <div className="info-item">
                                <label>Especialidades:</label>
                                <span>{formData.profile.specialties.length > 0 ? formData.profile.specialties.join(', ') : 'Não informado'}</span>
                            </div>
                            <div className="info-item">
                                <label>Experiência:</label>
                                <span>{formData.profile.experience || 'Não informado'}</span>
                            </div>
                            <div className="info-item">
                                <label>Certificações:</label>
                                <span>{formData.profile.certification || 'Não informado'}</span>
                            </div>
                        </div>
                    </div>

                    <div className="form-actions">
                        <button 
                            type="button" 
                            onClick={onClose}
                            className="btn btn-secondary"
                        >
                            Fechar
                        </button>
                    </div>
                </div>
            ) : (
                // Modo edição (para novos treinadores ou quando editMode = true)
                <form onSubmit={handleSubmit} className="trainer-form">
                {error && <div className="error-message">{error}</div>}

                <div className="form-section">
                    <h3>Informações de Login</h3>
                    
                    <div className="form-group">
                        <label htmlFor="username">Nome de usuário *</label>
                        <input
                            type="text"
                            id="username"
                            name="username"
                            value={formData.username}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="email">Email *</label>
                        <input
                            type="email"
                            id="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    {!isEditing && (
                        <>
                            <div className="form-group">
                                <label htmlFor="password">Senha *</label>
                                <input
                                    type="password"
                                    id="password"
                                    name="password"
                                    value={formData.password}
                                    onChange={handleChange}
                                    required={!isEditing}
                                    minLength="6"
                                />
                            </div>

                            <div className="form-group">
                                <label htmlFor="confirmPassword">Confirmar Senha *</label>
                                <input
                                    type="password"
                                    id="confirmPassword"
                                    name="confirmPassword"
                                    value={formData.confirmPassword}
                                    onChange={handleChange}
                                    required={!isEditing}
                                />
                            </div>
                        </>
                    )}
                </div>

                <div className="form-section">
                    <h3>Informações Pessoais</h3>
                    
                    <div className="form-row">
                        <div className="form-group">
                            <label htmlFor="profile.firstName">Nome</label>
                            <input
                                type="text"
                                id="profile.firstName"
                                name="profile.firstName"
                                value={formData.profile.firstName}
                                onChange={handleChange}
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="profile.lastName">Sobrenome</label>
                            <input
                                type="text"
                                id="profile.lastName"
                                name="profile.lastName"
                                value={formData.profile.lastName}
                                onChange={handleChange}
                            />
                        </div>
                    </div>

                    <div className="form-group">
                        <label htmlFor="profile.phone">Telefone</label>
                        <input
                            type="text"
                            id="profile.phone"
                            name="profile.phone"
                            value={formData.profile.phone}
                            onChange={handleChange}
                        />
                    </div>
                </div>

                <div className="form-section">
                    <h3>Informações Profissionais</h3>
                    
                    <div className="form-group">
                        <label>Especialidades</label>
                        <div className="specialties-grid">
                            {specialtyOptions.map(specialty => (
                                <label key={specialty} className="checkbox-label">
                                    <input
                                        type="checkbox"
                                        checked={formData.profile.specialties.includes(specialty)}
                                        onChange={() => handleSpecialtyChange(specialty)}
                                    />
                                    {specialty}
                                </label>
                            ))}
                        </div>
                    </div>

                    <div className="form-group">
                        <label htmlFor="profile.experience">Experiência</label>
                        <textarea
                            id="profile.experience"
                            name="profile.experience"
                            value={formData.profile.experience}
                            onChange={handleChange}
                            rows="3"
                            placeholder="Descreva a experiência profissional..."
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="profile.certification">Certificações</label>
                        <textarea
                            id="profile.certification"
                            name="profile.certification"
                            value={formData.profile.certification}
                            onChange={handleChange}
                            rows="3"
                            placeholder="Liste as certificações..."
                        />
                    </div>
                </div>

                <div className="form-actions">
                    <button 
                        type="button" 
                        onClick={() => {
                            if (isEditing) {
                                setEditMode(false);
                            } else {
                                onClose();
                            }
                        }}
                        className="btn btn-secondary"
                    >
                        Cancelar
                    </button>
                    <button 
                        type="submit" 
                        disabled={loading}
                        className="btn btn-primary"
                    >
                        {loading ? 'Salvando...' : (isEditing ? 'Atualizar' : 'Criar Personal')}
                    </button>
                </div>
            </form>
            )}
        </div>
    );
};

export default TrainerForm;
