import React, { useContext, useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { ClientContext } from '../contexts/ClientContext';
import clientService from '../services/client.service';
import MetricsHistory from '../components/metrics/MetricsHistory';
import WorkoutHistory from '../components/workouts/WorkoutHistory';

// Ícones SVG
const UserIcon = ({ size = 48, color = '#6366f1' }) => (
    <svg width={size} height={size} viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M12,4A4,4 0 0,1 16,8A4,4 0 0,1 12,12A4,4 0 0,1 8,8A4,4 0 0,1 12,4M12,14C16.42,14 20,15.79 20,18V20H4V18C4,15.79 7.58,14 12,14Z" fill={color}/>
    </svg>
);

const MetricsIcon = ({ size = 24, color = '#f59e0b' }) => (
    <svg width={size} height={size} viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M16,6L18.29,8.29L13.41,13.17L9.41,9.17L2,16.59L3.41,18L9.41,12L13.41,16L19.71,9.71L22,12V6H16Z" fill={color}/>
    </svg>
);

const WorkoutIcon = ({ size = 24, color = '#10b981' }) => (
    <svg width={size} height={size} viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M20.57,14.86L22,13.43L20.57,12L17,15.57L8.43,7L12,3.43L10.57,2L9.14,3.43L7.71,2L5.57,4.14L4.14,2.71L2.71,4.14L4.14,5.57L2,7.71L3.43,9.14L2,10.57L3.43,12L7,8.43L15.57,17L12,20.57L13.43,22L14.86,20.57L16.29,22L18.43,19.86L19.86,21.29L21.29,19.86L19.86,18.43L22,16.29L20.57,14.86Z" fill={color}/>
    </svg>
);

const BackIcon = ({ size = 20, color = '#6b7280' }) => (
    <svg width={size} height={size} viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M20,11V13H8L13.5,18.5L12.08,19.92L4.16,12L12.08,4.08L13.5,5.5L8,11H20Z" fill={color}/>
    </svg>
);

const ClientDetailPage = () => {
    const { clientId } = useParams();
    const { clients } = useContext(ClientContext);
    const [client, setClient] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchClientDetails = async () => {
            try {
                const response = await clientService.getClientById(clientId);
                setClient(response.data);
            } catch (err) {
                console.error('Erro ao buscar detalhes do cliente:', err);
                setError('Falha ao carregar detalhes do cliente');
            } finally {
                setLoading(false);
            }
        };

        fetchClientDetails();
    }, [clientId]);

    if (loading) {
        return (
            <div className="trainer-dashboard">
                <div className="loading">Carregando perfil do cliente...</div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="trainer-dashboard">
                <div className="error-message">{error}</div>
                <Link to="/dashboard" className="back-link">
                    <BackIcon /> Voltar ao Dashboard
                </Link>
            </div>
        );
    }

    return (
        <div className="trainer-dashboard">
            {/* Header com navegação de volta */}
            <div className="dashboard-header">
                <Link to="/dashboard" className="back-link">
                    <BackIcon /> Voltar ao Dashboard
                </Link>
                <h1>Perfil do Cliente</h1>
            </div>

            {client && (
                <>
                    {/* Informações básicas do cliente */}
                    <div className="client-profile-header">
                        <div className="client-info-card">
                            <div className="client-avatar">
                                <UserIcon size={64} />
                            </div>
                            <div className="client-basic-info">
                                <h2 className="client-name">{client.name}</h2>
                                <div className="client-details-grid">
                                    <div className="detail-item">
                                        <span className="detail-label">Peso:</span>
                                        <span className="detail-value">{client.weight} kg</span>
                                    </div>
                                    <div className="detail-item">
                                        <span className="detail-label">Altura:</span>
                                        <span className="detail-value">{client.height} cm</span>
                                    </div>
                                    <div className="detail-item">
                                        <span className="detail-label">Gordura Corporal:</span>
                                        <span className="detail-value">{client.bodyFat}%</span>
                                    </div>
                                    <div className="detail-item">
                                        <span className="detail-label">Massa Muscular:</span>
                                        <span className="detail-value">{client.muscleMass}%</span>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    {/* Seções de métricas e treinos */}
                    <div className="dashboard-content">
                        <div className="clients-section">
                            <div className="section-header">
                                <h2>
                                    <MetricsIcon />
                                    Histórico de Métricas
                                </h2>
                            </div>
                            <MetricsHistory clientId={clientId} />
                        </div>

                        <div className="workouts-section">
                            <div className="section-header">
                                <h2>
                                    <WorkoutIcon />
                                    Histórico de Treinos
                                </h2>
                            </div>
                            <WorkoutHistory clientId={clientId} />
                        </div>
                    </div>
                </>
            )}
        </div>
    );
};

export default ClientDetailPage;
