import React, { createContext, useState, useEffect } from 'react';
import { authService } from '../services/auth.service';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    useEffect(() => {
        // Check if user is logged in from localStorage
        const checkAuth = () => {
            try {
                const userData = authService.getCurrentUser();
                const token = localStorage.getItem('token');

                // Só considera autenticado se tem tanto userData quanto token
                if (userData && token) {
                    setUser(userData);
                    setIsAuthenticated(true);
                } else {
                    // Se não tem dados completos, limpar tudo
                    authService.logout();
                    setUser(null);
                    setIsAuthenticated(false);
                }
            } catch (error) {
                console.error('Erro ao verificar autenticação:', error);
                // Em caso de erro, limpar estado
                authService.logout();
                setUser(null);
                setIsAuthenticated(false);
            }
            setLoading(false);
        };

        checkAuth();
    }, []);

    const login = async (email, password) => {
        try {
            console.log('Login attempt with:', email);
            const userData = await authService.login(email, password);
            console.log('Login successful:', userData);
            setUser(userData);
            setIsAuthenticated(true);
            return userData;
        } catch (error) {
            console.error("Login error:", error);
            throw error;
        }
    };

    const logout = async () => {
        try {
            // Limpar dados do localStorage
            authService.logout();

            // Limpar estado do contexto
            setUser(null);
            setIsAuthenticated(false);

            console.log('Logout realizado com sucesso');
        } catch (error) {
            console.error("Logout error:", error);

            // Mesmo se houver erro, limpar o estado local
            setUser(null);
            setIsAuthenticated(false);

            throw error;
        }
    };

    const register = async (username, email, password) => {
        try {
            const response = await authService.register(username, email, password);
            // After registration, we'll need to log in
            return response;
        } catch (error) {
            console.error("Registration error:", error);
            throw error;
        }
    };

    // Helper function to check if the current user is an admin
    const isAdmin = user && user.role === 'admin';

    return (
        <AuthContext.Provider value={{ 
            user, 
            loading, 
            isAuthenticated,
            isAdmin,
            login, 
            logout, 
            register 
        }}>
            {children}
        </AuthContext.Provider>
    );
};
