import React, { useContext, useEffect, useState } from 'react';
import { useParams, Link, useHistory } from 'react-router-dom';
import { ClientContext } from '../contexts/ClientContext';
import { AuthContext } from '../contexts/AuthContext';
import * as clientService from '../services/client.service';
import * as metricsService from '../services/metrics.service';
import MetricsHistory from '../components/metrics/MetricsHistory';
import WorkoutHistory from '../components/workouts/WorkoutHistory';
import EvolutionChart from '../components/charts/EvolutionChart';

const ClientDetailPage = () => {
    const { clientId } = useParams();
    const { clients } = useContext(ClientContext);
    const { user } = useContext(AuthContext);
    const history = useHistory();
    const [client, setClient] = useState(null);
    const [metrics, setMetrics] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchClientDetails = async () => {
            try {
                console.log('üîç Buscando detalhes do cliente:', clientId);
                const response = await clientService.getClientById(clientId);
                console.log('‚úÖ Cliente encontrado:', response);
                setClient(response);

                // Buscar m√©tricas do cliente
                const metricsResponse = await metricsService.getMetricsByClientId(clientId);
                console.log('üìä M√©tricas encontradas:', metricsResponse);
                setMetrics(metricsResponse || []);
            } catch (err) {
                console.error('‚ùå Erro ao buscar cliente:', err);
                setError('Erro ao carregar detalhes do cliente');
            } finally {
                setLoading(false);
            }
        };

        if (clientId) {
            fetchClientDetails();
        }
    }, [clientId]);

    const handleGoBack = () => {
        if (user?.role === 'trainer') {
            history.push('/trainer/dashboard');
        } else {
            history.push('/clients');
        }
    };

    if (loading) {
        return (
            <div className="loading-container">
                <div className="loading-spinner"></div>
                <p>Carregando detalhes do cliente...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div className="error-container">
                <div className="error-icon">‚ö†Ô∏è</div>
                <h2>Erro</h2>
                <p>{error}</p>
                <button onClick={handleGoBack} className="btn btn-primary">
                    Voltar
                </button>
            </div>
        );
    }

    if (!client) {
        return (
            <div className="empty-state">
                <div className="empty-icon">üë§</div>
                <h2>Cliente n√£o encontrado</h2>
                <p>O cliente solicitado n√£o foi encontrado ou voc√™ n√£o tem permiss√£o para visualiz√°-lo.</p>
                <button onClick={handleGoBack} className="btn btn-primary">
                    Voltar
                </button>
            </div>
        );
    }

    return (
        <div className="client-detail-page">
            {/* Header */}
            <div className="page-header">
                <div className="header-content">
                    <button onClick={handleGoBack} className="back-button">
                        ‚Üê Voltar
                    </button>
                    <div className="header-info">
                        <h1>Detalhes do Cliente</h1>
                        <p className="subtitle">Informa√ß√µes completas e hist√≥rico</p>
                    </div>
                </div>
            </div>

            {/* Client Information */}
            <div className="client-info-section">
                <div className="client-header">
                    <div className="client-avatar-large">
                        {client.name?.charAt(0).toUpperCase() || '?'}
                    </div>
                    <div className="client-basic-info">
                        <h2>{client.name || 'Nome n√£o informado'}</h2>
                        <p className="client-email">{client.email || 'Email n√£o informado'}</p>
                        <p className="client-phone">{client.phone || 'Telefone n√£o informado'}</p>
                        <span className={`status-badge ${client.isActive !== false ? 'active' : 'inactive'}`}>
                            {client.isActive !== false ? 'Ativo' : 'Inativo'}
                        </span>
                    </div>
                    <div className="client-actions">
                        <Link 
                            to={`/workout-plan/new?client=${client._id}`} 
                            className="btn btn-primary"
                        >
                            <span className="icon">üí™</span>
                            Novo Treino
                        </Link>
                        <Link 
                            to={`/clients/${client._id}/edit`} 
                            className="btn btn-secondary"
                        >
                            <span className="icon">‚úèÔ∏è</span>
                            Editar
                        </Link>
                    </div>
                </div>

                {/* Physical Stats */}
                <div className="physical-stats">
                    <h3>Informa√ß√µes F√≠sicas Atuais</h3>
                    <div className="stats-grid">
                        <div className="stat-item">
                            <span className="stat-label">Peso</span>
                            <span className="stat-value">
                                {metrics && metrics.length > 0 
                                    ? `${Math.round(metrics[metrics.length - 1].weight)} kg`
                                    : 'N√£o informado'
                                }
                            </span>
                        </div>
                        <div className="stat-item">
                            <span className="stat-label">Altura</span>
                            <span className="stat-value">
                                {metrics && metrics.length > 0 
                                    ? `${(metrics[metrics.length - 1].height * 100).toFixed(0)} cm`
                                    : 'N√£o informado'
                                }
                            </span>
                        </div>
                        <div className="stat-item">
                            <span className="stat-label">Gordura Corporal</span>
                            <span className="stat-value">
                                {metrics && metrics.length > 0 
                                    ? `${Math.round(metrics[metrics.length - 1].bodyFatPercentage)}%`
                                    : 'N√£o informado'
                                }
                            </span>
                        </div>
                        <div className="stat-item">
                            <span className="stat-label">Massa Muscular</span>
                            <span className="stat-value">
                                {metrics && metrics.length > 0 
                                    ? `${metrics[metrics.length - 1].muscleMassPercentage.toFixed(1)}%`
                                    : 'N√£o informado'
                                }
                            </span>
                        </div>
                        {metrics && metrics.length > 0 && (
                            <div className="stat-item">
                                <span className="stat-label">IMC</span>
                                <span className="stat-value">
                                    {(metrics[metrics.length - 1].weight / 
                                      (metrics[metrics.length - 1].height ** 2)).toFixed(1)}
                                </span>
                            </div>
                        )}
                        <div className="stat-item">
                            <span className="stat-label">√öltima Atualiza√ß√£o</span>
                            <span className="stat-value">
                                {metrics && metrics.length > 0 
                                    ? new Date(metrics[metrics.length - 1].date).toLocaleDateString('pt-BR')
                                    : 'Sem dados'
                                }
                            </span>
                        </div>
                    </div>
                </div>

                {/* Goals and Notes */}
                {(client.goals || client.notes) && (
                    <div className="additional-info">
                        {client.goals && (
                            <div className="info-section">
                                <h4>Objetivos</h4>
                                <p>{client.goals}</p>
                            </div>
                        )}
                        {client.notes && (
                            <div className="info-section">
                                <h4>Observa√ß√µes</h4>
                                <p>{client.notes}</p>
                            </div>
                        )}
                    </div>
                )}
            </div>

            {/* Evolution Dashboard */}
            <div className="evolution-dashboard">
                <h3>üìä Dashboard de Evolu√ß√£o</h3>

                {metrics && metrics.length > 1 ? (
                    <>
                        <div className="evolution-charts-grid">
                            <EvolutionChart 
                                data={metrics}
                                metric="weight"
                                title="üìà Evolu√ß√£o do Peso"
                                color="#3B82F6"
                                unit="kg"
                            />

                            <EvolutionChart 
                                data={metrics}
                                metric="bodyFatPercentage"
                                title="üìä Evolu√ß√£o da Gordura Corporal"
                                color="#EF4444"
                                unit="%"
                            />

                            <EvolutionChart 
                                data={metrics}
                                metric="muscleMassPercentage"
                                title="üí™ Evolu√ß√£o da Massa Muscular"
                                color="#10B981"
                                unit="%"
                            />

                            <EvolutionChart 
                                data={metrics.map(m => ({
                                    ...m,
                                    bmi: m.weight / (m.height ** 2)
                                }))}
                                metric="bmi"
                                title="‚öñÔ∏è Evolu√ß√£o do IMC"
                                color="#8B5CF6"
                                unit=""
                            />
                        </div>

                        {/* Evolution Summary */}
                        <div className="evolution-summary">
                            <h4>üìã Resumo da Evolu√ß√£o ({metrics.length} medi√ß√µes)</h4>
                            <div className="summary-grid">
                                <div className="summary-item">
                                    <span className="summary-label">Per√≠odo de acompanhamento:</span>
                                    <span className="summary-value">
                                        {Math.ceil((new Date(metrics[metrics.length - 1].date) - new Date(metrics[0].date)) / (1000 * 60 * 60 * 24 * 30))} meses
                                    </span>
                                </div>
                                <div className="summary-item">
                                    <span className="summary-label">√öltima medi√ß√£o:</span>
                                    <span className="summary-value">
                                        {new Date(metrics[metrics.length - 1].date).toLocaleDateString('pt-BR')}
                                    </span>
                                </div>
                                <div className="summary-item">
                                    <span className="summary-label">Pr√≥xima avalia√ß√£o sugerida:</span>
                                    <span className="summary-value">
                                        {(() => {
                                            const nextDate = new Date(metrics[metrics.length - 1].date);
                                            nextDate.setMonth(nextDate.getMonth() + 1);
                                            return nextDate.toLocaleDateString('pt-BR');
                                        })()}
                                    </span>
                                </div>
                                <div className="summary-item">
                                    <span className="summary-label">Status geral:</span>
                                    <span className="summary-value">
                                        {(() => {
                                            const weightDiff = metrics[metrics.length - 1].weight - metrics[0].weight;
                                            const fatDiff = metrics[metrics.length - 1].bodyFatPercentage - metrics[0].bodyFatPercentage;
                                            const muscleDiff = metrics[metrics.length - 1].muscleMassPercentage - metrics[0].muscleMassPercentage;

                                            if (fatDiff < -2 && muscleDiff > 1) return "üéâ Excelente evolu√ß√£o!";
                                            if (fatDiff < 0 && muscleDiff > 0) return "‚úÖ Boa evolu√ß√£o";
                                            if (Math.abs(fatDiff) < 1 && Math.abs(muscleDiff) < 1) return "‚û°Ô∏è Mantendo";
                                            return "üìà Em progresso";
                                        })()}
                                    </span>
                                </div>
                            </div>
                        </div>
                    </>
                ) : (
                    <div className="no-evolution-data">
                        <div className="empty-icon">üìä</div>
                        <h4>Dados insuficientes para evolu√ß√£o</h4>
                        <p>S√£o necess√°rias pelo menos 2 medi√ß√µes para gerar o dashboard de evolu√ß√£o.</p>
                        <Link to={`/metrics/new?client=${clientId}`} className="btn btn-primary">
                            <span className="icon">üìù</span>
                            Adicionar Nova Medi√ß√£o
                        </Link>
                    </div>
                )}
            </div>

            {/* Metrics and Workout History */}
            <div className="history-sections">
                <div className="history-section">
                    <h3>Hist√≥rico de M√©tricas</h3>
                    <MetricsHistory clientId={clientId} />
                </div>

                <div className="history-section">
                    <h3>Hist√≥rico de Treinos</h3>
                    <WorkoutHistory clientId={clientId} />
                </div>
            </div>
        </div>
    );
};

export default ClientDetailPage;
