import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../contexts/AuthContext';

const MyProgressPage = () => {
    const { user } = useContext(AuthContext);
    const [clientData, setClientData] = useState(null);
    const [metrics, setMetrics] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        fetchMyProgress();
    }, []);

    const fetchMyProgress = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:5000/api/clients/my-data', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                }
            });

            if (!response.ok) {
                throw new Error('Falha ao carregar dados de progresso');
            }

            const data = await response.json();
            setClientData(data);
            setMetrics(data.metrics || []);
        } catch (err) {
            setError(err.message);
            console.error('Erro ao buscar progresso:', err);
        } finally {
            setLoading(false);
        }
    };

    const calculateBMI = (weight, height) => {
        if (!weight || !height) return null;
        return (weight / (height * height)).toFixed(1);
    };

    const getBMICategory = (bmi) => {
        if (bmi < 18.5) return 'Abaixo do peso';
        if (bmi < 25) return 'Peso normal';
        if (bmi < 30) return 'Sobrepeso';
        return 'Obesidade';
    };

    if (loading) {
        return <div className="loading">Carregando seu progresso...</div>;
    }

    if (error) {
        return <div className="error">Erro: {error}</div>;
    }

    const latestMetrics = metrics.length > 0 ? metrics[metrics.length - 1] : null;

    return (
        <div className="my-progress-page">
            <div className="page-header">
                <h1>Meu Progresso</h1>
                <p>Acompanhe sua evolução e métricas corporais</p>
            </div>

            {clientData && (
                <div className="client-info">
                    <h2>Dados Pessoais</h2>
                    <div className="info-grid">
                        <div className="info-item">
                            <strong>Nome:</strong> {clientData.name}
                        </div>
                        <div className="info-item">
                            <strong>Email:</strong> {clientData.email}
                        </div>
                        <div className="info-item">
                            <strong>Telefone:</strong> {clientData.phone}
                        </div>
                        <div className="info-item">
                            <strong>Data de Cadastro:</strong> {new Date(clientData.createdAt).toLocaleDateString('pt-BR')}
                        </div>
                    </div>
                </div>
            )}

            {latestMetrics && (
                <div className="current-metrics">
                    <h2>Métricas Atuais</h2>
                    <div className="metrics-grid">
                        <div className="metric-card">
                            <h3>Peso</h3>
                            <div className="metric-value">{latestMetrics.weight} kg</div>
                        </div>
                        <div className="metric-card">
                            <h3>Altura</h3>
                            <div className="metric-value">{latestMetrics.height} m</div>
                        </div>
                        <div className="metric-card">
                            <h3>IMC</h3>
                            <div className="metric-value">
                                {calculateBMI(latestMetrics.weight, latestMetrics.height)}
                            </div>
                            <div className="metric-category">
                                {getBMICategory(calculateBMI(latestMetrics.weight, latestMetrics.height))}
                            </div>
                        </div>
                        {latestMetrics.bodyFatPercentage && (
                            <div className="metric-card">
                                <h3>% Gordura</h3>
                                <div className="metric-value">{latestMetrics.bodyFatPercentage}%</div>
                            </div>
                        )}
                        {latestMetrics.muscleMassPercentage && (
                            <div className="metric-card">
                                <h3>% Massa Muscular</h3>
                                <div className="metric-value">{latestMetrics.muscleMassPercentage}%</div>
                            </div>
                        )}
                    </div>
                    <div className="last-update">
                        <small>Última atualização: {new Date(latestMetrics.date).toLocaleDateString('pt-BR')}</small>
                    </div>
                </div>
            )}

            {metrics.length > 1 && (
                <div className="metrics-history">
                    <h2>Histórico de Métricas</h2>
                    <div className="history-table">
                        <table>
                            <thead>
                                <tr>
                                    <th>Data</th>
                                    <th>Peso (kg)</th>
                                    <th>IMC</th>
                                    <th>% Gordura</th>
                                    <th>% Massa Muscular</th>
                                </tr>
                            </thead>
                            <tbody>
                                {metrics.slice().reverse().map((metric, index) => (
                                    <tr key={index}>
                                        <td>{new Date(metric.date).toLocaleDateString('pt-BR')}</td>
                                        <td>{metric.weight}</td>
                                        <td>{calculateBMI(metric.weight, metric.height)}</td>
                                        <td>{metric.bodyFatPercentage || '-'}</td>
                                        <td>{metric.muscleMassPercentage || '-'}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            )}

            {metrics.length === 0 && (
                <div className="no-metrics">
                    <h2>Nenhuma métrica encontrada</h2>
                    <p>Seu treinador ainda não registrou suas métricas corporais.</p>
                    <p>Entre em contato para iniciar o acompanhamento do seu progresso!</p>
                </div>
            )}
        </div>
    );
};

export default MyProgressPage;
