import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../contexts/AuthContext';
import EvolutionChart from '../components/charts/EvolutionChart';
import MetricsHistory from '../components/metrics/MetricsHistory';
import WorkoutHistory from '../components/workouts/WorkoutHistory';

const MyProgressPage = () => {
    const { user } = useContext(AuthContext);
    const [clientData, setClientData] = useState(null);
    const [metrics, setMetrics] = useState([]);
    const [workouts, setWorkouts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [stats, setStats] = useState({
        totalWorkouts: 0,
        completedWorkouts: 0,
        lastWeightChange: 0,
        progressPercentage: 0
    });

    useEffect(() => {
        fetchMyProgress();
    }, []);

    const fetchMyProgress = async () => {
        try {
            const token = localStorage.getItem('token');
            
            // Buscar dados do cliente
            const response = await fetch('http://localhost:5000/api/clients/my-data', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                }
            });

            if (!response.ok) {
                throw new Error('Falha ao carregar dados de progresso');
            }

            const data = await response.json();
            setClientData(data);
            setMetrics(data.metrics || []);
            
            // Calcular estatísticas
            calculateStats(data.metrics || []);

        } catch (err) {
            setError(err.message);
            console.error('Erro ao buscar progresso:', err);
        } finally {
            setLoading(false);
        }
    };

    const calculateStats = (metricsData) => {
        if (metricsData.length >= 2) {
            const latest = metricsData[metricsData.length - 1];
            const previous = metricsData[metricsData.length - 2];
            const weightChange = latest.weight - previous.weight;
            
            setStats(prev => ({
                ...prev,
                lastWeightChange: weightChange,
                progressPercentage: Math.round(((metricsData.length - 1) / 10) * 100) // Assumindo meta de 10 medições
            }));
        }
    };

    const calculateBMI = (weight, height) => {
        if (!weight || !height) return null;
        return (weight / (height * height)).toFixed(1);
    };

    const getBMICategory = (bmi) => {
        if (bmi < 18.5) return 'Abaixo do peso';
        if (bmi < 25) return 'Peso normal';
        if (bmi < 30) return 'Sobrepeso';
        return 'Obesidade';
    };

    const getBMIColor = (bmi) => {
        if (bmi < 18.5) return '#f39c12';
        if (bmi < 25) return '#27ae60';
        if (bmi < 30) return '#f39c12';
        return '#e74c3c';
    };

    const getProgressColor = (value) => {
        if (value > 0) return '#27ae60';
        if (value < 0) return '#e74c3c';
        return '#95a5a6';
    };

    const formatProgressValue = (value) => {
        if (value > 0) return `+${value.toFixed(1)}`;
        return value.toFixed(1);
    };

    if (loading) {
        return (
            <div className="my-progress-page">
                <div className="loading-container">
                    <div className="loading-spinner"></div>
                    <p>Carregando seu progresso...</p>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="my-progress-page">
                <div className="error-banner">
                    <span>⚠️</span>
                    <span>Erro: {error}</span>
                </div>
            </div>
        );
    }

    const latestMetrics = metrics.length > 0 ? metrics[metrics.length - 1] : null;
    const currentBMI = latestMetrics ? calculateBMI(latestMetrics.weight, latestMetrics.height) : null;

    return (
        <div className="my-progress-page">
            {/* Dashboard Header */}
            <div className="dashboard-header">
                <div className="header-content">
                    <div className="client-info">
                        <h1>Meu Progresso</h1>
                        <p className="welcome-text">Olá, {clientData?.name || user?.name}!</p>
                        <p className="subtitle">Acompanhe sua evolução e conquiste suas metas</p>
                    </div>
                    <div className="client-avatar">
                        <div className="avatar-circle">
                            {(clientData?.name || user?.name)?.charAt(0).toUpperCase()}
                        </div>
                    </div>
                </div>
            </div>

            {/* Stats Grid */}
            <div className="stats-grid">
                <div className="stat-card">
                    <div className="stat-icon">⚖️</div>
                    <div className="stat-info">
                        <h3>Peso Atual</h3>
                        <div className="stat-value">
                            {latestMetrics ? `${Math.round(latestMetrics.weight)} kg` : 'N/A'}
                        </div>
                        {stats.lastWeightChange !== 0 && (
                            <div 
                                className="stat-change"
                                style={{ color: getProgressColor(stats.lastWeightChange) }}
                            >
                                {formatProgressValue(stats.lastWeightChange)} kg
                            </div>
                        )}
                    </div>
                </div>

                <div className="stat-card">
                    <div className="stat-icon">📊</div>
                    <div className="stat-info">
                        <h3>IMC</h3>
                        <div className="stat-value">
                            {currentBMI || 'N/A'}
                        </div>
                        {currentBMI && (
                            <div 
                                className="stat-change"
                                style={{ color: getBMIColor(parseFloat(currentBMI)) }}
                            >
                                {getBMICategory(parseFloat(currentBMI))}
                            </div>
                        )}
                    </div>
                </div>

                <div className="stat-card">
                    <div className="stat-icon">💪</div>
                    <div className="stat-info">
                        <h3>% Gordura</h3>
                        <div className="stat-value">
                            {latestMetrics?.bodyFatPercentage ? `${Math.round(latestMetrics.bodyFatPercentage)}%` : 'N/A'}
                        </div>
                        <div className="stat-change">Corporal</div>
                    </div>
                </div>

                <div className="stat-card">
                    <div className="stat-icon">🎯</div>
                    <div className="stat-info">
                        <h3>Progresso</h3>
                        <div className="stat-value">{Math.round(stats.progressPercentage)}%</div>
                        <div className="stat-change">Meta</div>
                    </div>
                </div>
            </div>

            {/* Main Content Grid */}
            <div className="main-content-grid">
                {/* Evolution Charts */}
                <div className="content-section">
                    <div className="section-header">
                        <h2>Evolução das Métricas</h2>
                        <p>Acompanhe sua progressão ao longo do tempo</p>
                    </div>
                    
                    {metrics.length >= 2 ? (
                        <div className="charts-grid">
                            <div className="chart-card">
                                <h3>Evolução do Peso</h3>
                                <EvolutionChart 
                                    data={metrics} 
                                    metric="weight" 
                                    title="Peso (kg)" 
                                    color="#3498db" 
                                    unit="kg"
                                />
                            </div>
                            
                            {metrics.some(m => m.bodyFatPercentage) && (
                                <div className="chart-card">
                                    <h3>% Gordura Corporal</h3>
                                    <EvolutionChart 
                                        data={metrics.filter(m => m.bodyFatPercentage)} 
                                        metric="bodyFatPercentage" 
                                        title="Gordura (%)" 
                                        color="#e74c3c" 
                                        unit="%"
                                    />
                                </div>
                            )}
                            
                            {metrics.some(m => m.muscleMassPercentage) && (
                                <div className="chart-card">
                                    <h3>% Massa Muscular</h3>
                                    <EvolutionChart 
                                        data={metrics.filter(m => m.muscleMassPercentage)} 
                                        metric="muscleMassPercentage" 
                                        title="Músculo (%)" 
                                        color="#27ae60" 
                                        unit="%"
                                    />
                                </div>
                            )}
                        </div>
                    ) : (
                        <div className="empty-state">
                            <div className="empty-icon">📈</div>
                            <h4>Dados insuficientes</h4>
                            <p>São necessárias pelo menos 2 medições para gerar gráficos de evolução.</p>
                        </div>
                    )}
                </div>

                {/* Metrics History */}
                <div className="content-section">
                    <div className="section-header">
                        <h2>Histórico de Métricas</h2>
                        <p>Todas as suas medições organizadas</p>
                    </div>
                    
                    {metrics.length > 0 ? (
                        <MetricsHistory metrics={metrics} />
                    ) : (
                        <div className="empty-state">
                            <div className="empty-icon">📋</div>
                            <h4>Nenhuma métrica encontrada</h4>
                            <p>Seu treinador ainda não registrou suas métricas corporais.</p>
                            <p>Entre em contato para iniciar o acompanhamento!</p>
                        </div>
                    )}
                </div>

                {/* Workout History */}
                {clientData?._id && (
                    <div className="content-section">
                        <div className="section-header">
                            <h2>Histórico de Treinos</h2>
                            <p>Seus treinos e exercícios</p>
                        </div>
                        <WorkoutHistory clientId={clientData._id} />
                    </div>
                )}
            </div>
        </div>
    );
};

export default MyProgressPage;
