import React, { useState, useContext } from 'react';
import { ClientContext } from '../../../contexts/ClientContext';

const ClientForm = ({ client }) => {
    const { addClient, updateClient } = useContext(ClientContext);
    const [name, setName] = useState(client ? client.name : '');
    const [age, setAge] = useState(client ? client.age : '');
    const [weight, setWeight] = useState(client ? client.weight : '');
    const [height, setHeight] = useState(client ? client.height : '');

    const handleSubmit = (e) => {
        e.preventDefault();
        const clientData = { name, age, weight, height };

        if (client) {
            updateClient(client.id, clientData);
        } else {
            addClient(clientData);
        }

        // Clear form fields after submission
        setName('');
        setAge('');
        setWeight('');
        setHeight('');
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>Name:</label>
                <input
                    type="text"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                />
            </div>
            <div>
                <label>Age:</label>
                <input
                    type="number"
                    value={age}
                    onChange={(e) => setAge(e.target.value)}
                    required
                />
            </div>
            <div>
                <label>Weight:</label>
                <input
                    type="number"
                    value={weight}
                    onChange={(e) => setWeight(e.target.value)}
                    required
                />
            </div>
            <div>
                <label>Height:</label>
                <input
                    type="number"
                    value={height}
                    onChange={(e) => setHeight(e.target.value)}
                    required
                />
            </div>
            <button type="submit">{client ? 'Update Client' : 'Add Client'}</button>
        </form>
    );
};

export default ClientForm;
