import axios from 'axios';
import { isTokenExpired } from '../utils/httpInterceptor';

const API_URL = 'http://localhost:5000/api/auth/';

const register = async (userData) => {
    try {
        const response = await axios.post(API_URL + 'register', userData);
        return response.data;
    } catch (error) {
        console.error('Registration error:', error.response?.data || error.message);
        throw error;
    }
};

const login = async (email, password) => {
    try {
        const response = await axios.post(API_URL + 'login', {
            email,
            password,
        });

        if (response.data.token) {
            // Store user info and token in localStorage
            localStorage.setItem('token', response.data.token);
            localStorage.setItem('user', JSON.stringify(response.data.user));
            localStorage.setItem('userRole', response.data.user.role || 'user');
        }
        return response.data.user;
    } catch (error) {
        console.error('Login error:', error.response?.data || error.message);
        throw error;
    }
};

const logout = () => {
    // Limpar todos os dados relacionados  autentica칞칚o
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    localStorage.removeItem('userRole');

    // Limpar qualquer cache do axios se existir
    delete axios.defaults.headers.common['Authorization'];

    console.log('Dados de autentica칞칚o removidos do localStorage');
};

const getCurrentUser = () => {
    try {
        const userStr = localStorage.getItem('user');
        const token = localStorage.getItem('token');

        // Verificar se o token existe e n칚o est치 expirado
        if (userStr && token && !isTokenExpired(token)) {
            return JSON.parse(userStr);
        }

        // Se token expirado, limpar dados
        if (token && isTokenExpired(token)) {
            console.log('游 Token expirado detectado. Limpando dados de autentica칞칚o...');
            logout();
        }

        return null;
    } catch (error) {
        console.error('Erro ao recuperar usu치rio do localStorage:', error);
        // Se h치 erro ao parsear, limpar dados corrompidos
        logout();
        return null;
    }
};

const isAuthenticated = () => {
    const token = localStorage.getItem('token');
    const user = localStorage.getItem('user');

    // Verificar se existe token, usu치rio e se o token n칚o est치 expirado
    if (!token || !user) {
        return false;
    }

    if (isTokenExpired(token)) {
        console.log('游 Token expirado. Usu치rio n칚o est치 autenticado.');
        logout();
        return false;
    }

    return true;
};

// Add auth state change listener for AuthContext
const onAuthStateChanged = (callback) => {
    const user = getCurrentUser();
    callback(user);
    return () => {}; // Return empty function for unsubscribe
};

export const authService = {
    register,
    login,
    logout,
    getCurrentUser,
    isAuthenticated,
    onAuthStateChanged
};
