import React, { useContext, useEffect, useState } from 'react';
import { useParams, Link, useHistory } from 'react-router-dom';
import { ClientContext } from '../contexts/ClientContext';
import { AuthContext } from '../contexts/AuthContext';
import * as clientService from '../services/client.service';
import MetricsHistory from '../components/metrics/MetricsHistory';
import WorkoutHistory from '../components/workouts/WorkoutHistory';

const ClientDetailPage = () => {
    const { clientId } = useParams();
    const { clients } = useContext(ClientContext);
    const { user } = useContext(AuthContext);
    const history = useHistory();
    const [client, setClient] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchClientDetails = async () => {
            try {
                console.log('üîç Buscando detalhes do cliente:', clientId);
                const response = await clientService.getClientById(clientId);
                console.log('‚úÖ Cliente encontrado:', response);
                setClient(response);
            } catch (err) {
                console.error('‚ùå Erro ao buscar cliente:', err);
                setError('Erro ao carregar detalhes do cliente');
            } finally {
                setLoading(false);
            }
        };

        if (clientId) {
            fetchClientDetails();
        }
    }, [clientId]);

    const handleGoBack = () => {
        if (user?.role === 'trainer') {
            history.push('/trainer/dashboard');
        } else {
            history.push('/clients');
        }
    };

    if (loading) {
        return (
            <div className="loading-container">
                <div className="loading-spinner"></div>
                <p>Carregando detalhes do cliente...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div className="error-container">
                <div className="error-icon">‚ö†Ô∏è</div>
                <h2>Erro</h2>
                <p>{error}</p>
                <button onClick={handleGoBack} className="btn btn-primary">
                    Voltar
                </button>
            </div>
        );
    }

    if (!client) {
        return (
            <div className="empty-state">
                <div className="empty-icon">üë§</div>
                <h2>Cliente n√£o encontrado</h2>
                <p>O cliente solicitado n√£o foi encontrado ou voc√™ n√£o tem permiss√£o para visualiz√°-lo.</p>
                <button onClick={handleGoBack} className="btn btn-primary">
                    Voltar
                </button>
            </div>
        );
    }

    return (
        <div className="client-detail-page">
            {/* Header */}
            <div className="page-header">
                <div className="header-content">
                    <button onClick={handleGoBack} className="back-button">
                        ‚Üê Voltar
                    </button>
                    <div className="header-info">
                        <h1>Detalhes do Cliente</h1>
                        <p className="subtitle">Informa√ß√µes completas e hist√≥rico</p>
                    </div>
                </div>
            </div>

            {/* Client Information */}
            <div className="client-info-section">
                <div className="client-header">
                    <div className="client-avatar-large">
                        {client.name?.charAt(0).toUpperCase() || '?'}
                    </div>
                    <div className="client-basic-info">
                        <h2>{client.name || 'Nome n√£o informado'}</h2>
                        <p className="client-email">{client.email || 'Email n√£o informado'}</p>
                        <p className="client-phone">{client.phone || 'Telefone n√£o informado'}</p>
                        <span className={`status-badge ${client.isActive !== false ? 'active' : 'inactive'}`}>
                            {client.isActive !== false ? 'Ativo' : 'Inativo'}
                        </span>
                    </div>
                    <div className="client-actions">
                        <Link 
                            to={`/workout-plan/new?client=${client._id}`} 
                            className="btn btn-primary"
                        >
                            <span className="icon">üí™</span>
                            Novo Treino
                        </Link>
                        <Link 
                            to={`/clients/${client._id}/edit`} 
                            className="btn btn-secondary"
                        >
                            <span className="icon">‚úèÔ∏è</span>
                            Editar
                        </Link>
                    </div>
                </div>

                {/* Physical Stats */}
                <div className="physical-stats">
                    <h3>Informa√ß√µes F√≠sicas</h3>
                    <div className="stats-grid">
                        <div className="stat-item">
                            <span className="stat-label">Peso</span>
                            <span className="stat-value">{client.weight ? `${client.weight} kg` : 'N√£o informado'}</span>
                        </div>
                        <div className="stat-item">
                            <span className="stat-label">Altura</span>
                            <span className="stat-value">{client.height ? `${client.height} cm` : 'N√£o informado'}</span>
                        </div>
                        <div className="stat-item">
                            <span className="stat-label">Gordura Corporal</span>
                            <span className="stat-value">{client.bodyFat ? `${client.bodyFat}%` : 'N√£o informado'}</span>
                        </div>
                        <div className="stat-item">
                            <span className="stat-label">Massa Muscular</span>
                            <span className="stat-value">{client.muscleMass ? `${client.muscleMass}%` : 'N√£o informado'}</span>
                        </div>
                        {client.weight && client.height && (
                            <div className="stat-item">
                                <span className="stat-label">IMC</span>
                                <span className="stat-value">
                                    {(client.weight / ((client.height / 100) ** 2)).toFixed(1)}
                                </span>
                            </div>
                        )}
                        <div className="stat-item">
                            <span className="stat-label">Data de Nascimento</span>
                            <span className="stat-value">
                                {client.dateOfBirth 
                                    ? new Date(client.dateOfBirth).toLocaleDateString('pt-BR')
                                    : 'N√£o informado'
                                }
                            </span>
                        </div>
                    </div>
                </div>

                {/* Goals and Notes */}
                {(client.goals || client.notes) && (
                    <div className="additional-info">
                        {client.goals && (
                            <div className="info-section">
