import React, { useEffect, useState } from 'react';
import { getWorkoutsByClientId } from '../../services/workout.service';

// √çcone SVG para treino
const WorkoutIcon = ({ size = 16, color = '#10b981' }) => (
    <svg width={size} height={size} viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M20.57,14.86L22,13.43L20.57,12L17,15.57L8.43,7L12,3.43L10.57,2L9.14,3.43L7.71,2L5.57,4.14L4.14,2.71L2.71,4.14L4.14,5.57L2,7.71L3.43,9.14L2,10.57L3.43,12L7,8.43L15.57,17L12,20.57L13.43,22L14.86,20.57L16.29,22L18.43,19.86L19.86,21.29L21.29,19.86L19.86,18.43L22,16.29L20.57,14.86Z" fill={color}/>
    </svg>
);

const WorkoutHistory = ({ clientId }) => {
    const [workouts, setWorkouts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchWorkoutHistory = async () => {
            try {
                setLoading(true);
                console.log('üèãÔ∏è Buscando treinos para cliente:', clientId);
                const workoutsData = await getWorkoutsByClientId(clientId);
                console.log('‚úÖ Treinos encontrados:', workoutsData);
                setWorkouts(workoutsData || []);
            } catch (err) {
                console.error('‚ùå Erro ao buscar treinos:', err);
                setError(err.message || 'Erro ao carregar treinos');
            } finally {
                setLoading(false);
            }
        };

        if (clientId) {
            fetchWorkoutHistory();
        }
    }, [clientId]);

    if (loading) {
        return (
            <div className="loading-container">
                <div className="loading-spinner"></div>
                <p>Carregando hist√≥rico de treinos...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div className="error-message">
                <p>Erro ao carregar treinos: {error}</p>
            </div>
        );
    }

    if (!workouts || workouts.length === 0) {
        return (
            <div className="empty-state">
                <p>Nenhum plano de treino atribu√≠do ainda.</p>
                <small>Os treinos aparecer√£o aqui quando forem criados e atribu√≠dos a este cliente.</small>
            </div>
        );
    }

    // Ordenar treinos por data de cria√ß√£o (mais recente primeiro)
    const sortedWorkouts = workouts.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));

    return (
        <div className="workout-history">
            <div className="workouts-list">
                {sortedWorkouts.map((workout, index) => (
                    <div key={workout._id || index} className="workout-card">
                        <div className="workout-header">
                            <div className="workout-title">
                                <WorkoutIcon size={20} />
                                <h4>{workout.name || workout.title || 'Treino sem nome'}</h4>
                            </div>
                            <div className="workout-date">
                                {workout.createdAt 
                                    ? new Date(workout.createdAt).toLocaleDateString('pt-BR')
                                    : 'Data n√£o informada'
                                }
                            </div>
                        </div>

                        {workout.description && (
                            <div className="workout-description">
                                <p>{workout.description}</p>
                            </div>
                        )}

                        {workout.exercises && workout.exercises.length > 0 && (
                            <div className="workout-exercises">
                                <h5>Exerc√≠cios ({workout.exercises.length})</h5>
                                <div className="exercises-grid">
                                    {workout.exercises.slice(0, 3).map((exercise, idx) => (
                                        <div key={idx} className="exercise-item">
                                            <span className="exercise-name">{exercise.name}</span>
                                            {exercise.sets && exercise.reps && (
                                                <span className="exercise-details">
                                                    {exercise.sets}x{exercise.reps}
                                                </span>
                                            )}
                                        </div>
                                    ))}
                                    {workout.exercises.length > 3 && (
                                        <div className="exercise-item more">
                                            +{workout.exercises.length - 3} mais
                                        </div>
                                    )}
                                </div>
                            </div>
                        )}

                        <div className="workout-footer">
                            <span className="workout-status">
                                {workout.isActive !== false ? 'Ativo' : 'Inativo'}
                            </span>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default WorkoutHistory;
