import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../contexts/AuthContext';
import EvolutionChart from '../components/charts/EvolutionChart';
import MetricsHistory from '../components/metrics/MetricsHistory';
import WorkoutHistory from '../components/workouts/WorkoutHistory';
import { 
    WeightIcon, 
    ChartIcon, 
    TargetIcon, 
    FireIcon, 
    DumbbellIcon, 
    UserIcon, 
    CalendarIcon, 
    BoltIcon, 
    TrophyIcon, 
    FlagIcon, 
    TrendingUpIcon, 
    CloseIcon, 
    HeartIcon, 
    CheckIcon, 
    LightbulbIcon, 
    EyeIcon, 
    PhoneIcon, 
    GroupIcon, 
    StarIcon 
} from '../components/icons/SvgIcons';

const MyProgressPage = () => {
    const { user } = useContext(AuthContext);
    const [clientData, setClientData] = useState(null);
    const [metrics, setMetrics] = useState([]);
    const [workouts, setWorkouts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [activeTab, setActiveTab] = useState('overview');
    const [animateCards, setAnimateCards] = useState(false);
    const [showMotivation, setShowMotivation] = useState(true);
    const [currentTime, setCurrentTime] = useState(new Date());
    const [stats, setStats] = useState({
        totalWorkouts: 0,
        completedWorkouts: 0,
        lastWeightChange: 0,
        progressPercentage: 0,
        weeklyProgress: 0,
        monthlyGoal: 0
    });

    useEffect(() => {
        fetchMyProgress();
        // Trigger card animation after mount
        setTimeout(() => setAnimateCards(true), 100);
        
        // Update time every minute for dynamic greeting
        const timeInterval = setInterval(() => {
            setCurrentTime(new Date());
        }, 60000);

        return () => clearInterval(timeInterval);
    }, []);

    const fetchMyProgress = async () => {
        try {
            const token = localStorage.getItem('token');
            
            // Buscar dados do cliente
            const response = await fetch('http://localhost:5000/api/clients/my-data', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                }
            });

            if (!response.ok) {
                throw new Error('Falha ao carregar dados de progresso');
            }

            const data = await response.json();
            console.log('üìä Dados recebidos do cliente:', data);
            setClientData(data);
            setMetrics(data.metrics || []);
            
            // Calcular estat√≠sticas
            calculateStats(data.metrics || []);

        } catch (err) {
            setError(err.message);
            console.error('Erro ao buscar progresso:', err);
        } finally {
            setLoading(false);
        }
    };

    const calculateStats = (metricsData) => {
        if (metricsData.length >= 2) {
            const latest = metricsData[metricsData.length - 1];
            const previous = metricsData[metricsData.length - 2];
            const weightChange = latest.weight - previous.weight;
            
            // Calculate weekly progress
            const lastWeek = metricsData.filter(m => {
                const metricDate = new Date(m.date);
                const weekAgo = new Date();
                weekAgo.setDate(weekAgo.getDate() - 7);
                return metricDate >= weekAgo;
            });
            
            const weeklyProgress = lastWeek.length;
            const monthlyGoal = Math.round((metricsData.length / 30) * 100);
            
            setStats(prev => ({
                ...prev,
                lastWeightChange: weightChange,
                progressPercentage: Math.round(((metricsData.length - 1) / 10) * 100), // Assumindo meta de 10 medi√ß√µes
                weeklyProgress: weeklyProgress,
                monthlyGoal: Math.min(monthlyGoal, 100)
            }));
        }
    };

    const calculateBMI = (weight, height) => {
        if (!weight || !height) return null;
        return Math.round(weight / (height * height));
    };

    const getBMICategory = (bmi) => {
        if (bmi < 18.5) return 'Abaixo do peso';
        if (bmi < 25) return 'Peso normal';
        if (bmi < 30) return 'Sobrepeso';
        return 'Obesidade';
    };

    const getBMIColor = (bmi) => {
        if (bmi < 18.5) return '#f39c12';
        if (bmi < 25) return '#27ae60';
        if (bmi < 30) return '#f39c12';
        return '#e74c3c';
    };

    const getProgressColor = (value) => {
        if (value > 0) return '#27ae60';
        if (value < 0) return '#e74c3c';
        return '#95a5a6';
    };

    const formatProgressValue = (value) => {
        const roundedValue = Math.round(value);
        if (roundedValue > 0) return `+${roundedValue}`;
        return roundedValue.toString();
    };

    const getMotivationalMessage = () => {
        const hour = currentTime.getHours();
        const dayMessages = {
            morning: [
                "Bom dia! Comece o dia com energia!",
                "Uma nova manh√£, novas oportunidades!",
                "Voc√™ est√° mais forte que ontem!",
                "O sucesso come√ßa com o primeiro passo!"
            ],
            afternoon: [
                "Boa tarde! Continue firme nos seus objetivos!",
                "Metade do dia j√° passou, voc√™ est√° indo bem!",
                "Cada movimento conta para o seu progresso!",
                "Sua dedica√ß√£o est√° transformando voc√™!"
            ],
            evening: [
                "Boa noite! Reflita sobre seus avan√ßos hoje!",
                "O dia foi produtivo? Amanh√£ ser√° ainda melhor!",
                "Descanso tamb√©m √© parte do progresso!",
                "Voc√™ est√° no caminho certo! Continue!"
            ]
        };

        let timeOfDay;
        if (hour < 12) timeOfDay = 'morning';
        else if (hour < 18) timeOfDay = 'afternoon';
        else timeOfDay = 'evening';

        const messages = dayMessages[timeOfDay];
        return messages[Math.floor(Math.random() * messages.length)];
    };

    const getTimeGreeting = () => {
        const hour = currentTime.getHours();
        if (hour < 12) return "Bom dia";
        if (hour < 18) return "Boa tarde";
        return "Boa noite";
    };

    const getStreakIcon = (streak) => {
        if (streak >= 30) return TrophyIcon;
        if (streak >= 14) return FireIcon;
        if (streak >= 7) return StarIcon;
        if (streak >= 3) return DumbbellIcon;
        return HeartIcon;
    };

    const getStreakInfo = () => {
        if (metrics.length === 0) return { streak: 0, message: "Comece hoje sua jornada!" };
        
        const daysSinceLastMetric = Math.floor((new Date() - new Date(metrics[metrics.length - 1].date)) / (1000 * 60 * 60 * 24));
        
        if (daysSinceLastMetric <= 7) {
            return { streak: metrics.length, message: "Voc√™ est√° mantendo o ritmo!" };
        } else {
            return { streak: 0, message: "Hora de retomar os registros!" };
        }
    };

    const handleTabChange = (newTab) => {
        setActiveTab(newTab);
        // Add subtle animation when switching tabs
        setAnimateCards(false);
        setTimeout(() => setAnimateCards(true), 50);
    };

    const getAchievementBadges = () => {
        const badges = [];
        const streakInfo = getStreakInfo();
        
        if (streakInfo.streak >= 7) badges.push({ icon: FireIcon, text: "Uma Semana!" });
        if (streakInfo.streak >= 30) badges.push({ icon: TrophyIcon, text: "Um M√™s!" });
        if (metrics.length >= 10) badges.push({ icon: ChartIcon, text: "Consistente" });
        if (stats.lastWeightChange < 0) badges.push({ icon: TrendingUpIcon, text: "Progresso" });
        
        return badges;
    };

    const renderTabContent = () => {
        switch (activeTab) {
            case 'overview':
                return renderOverviewTab();
            case 'metrics':
                return renderMetricsTab();
            case 'workouts':
                return renderWorkoutsTab();
            case 'goals':
                return renderGoalsTab();
            default:
                return renderOverviewTab();
        }
    };

    const renderOverviewTab = () => (
        <div className="overview-content">
            {/* Quick Stats Cards */}
            <div className="quick-stats-grid">
                <div className={`stat-card weight ${animateCards ? 'animate' : ''}`}>
                    <div className="stat-header">
                        <div className="stat-icon">
                            <WeightIcon size={28} color="#3498db" />
                        </div>
                        <h3>Peso Atual</h3>
                    </div>
                    <div className="stat-content">
                        <div className="stat-value">
                            {latestMetrics ? `${Math.round(latestMetrics.weight)} kg` : 'N/A'}
                        </div>
                        {stats.lastWeightChange !== 0 && (
                            <div 
                                className="stat-change"
                                style={{ color: getProgressColor(stats.lastWeightChange) }}
                            >
                                {formatProgressValue(stats.lastWeightChange)} kg
                            </div>
                        )}
                    </div>
                </div>

                <div className={`stat-card bmi ${animateCards ? 'animate' : ''}`}>
                    <div className="stat-header">
                        <div className="stat-icon">
                            <ChartIcon size={28} color="#e67e22" />
                        </div>
                        <h3>IMC</h3>
                    </div>
                    <div className="stat-content">
                        <div className="stat-value">{currentBMI || 'N/A'}</div>
                        {currentBMI && (
                            <div 
                                className="stat-change"
                                style={{ color: getBMIColor(parseFloat(currentBMI)) }}
                            >
                                {getBMICategory(parseFloat(currentBMI))}
                            </div>
                        )}
                    </div>
                </div>

                <div className={`stat-card progress ${animateCards ? 'animate' : ''}`}>
                    <div className="stat-header">
                        <div className="stat-icon">
                            <TargetIcon size={28} color="#27ae60" />
                        </div>
                        <h3>Progresso</h3>
                    </div>
                    <div className="stat-content">
                        <div className="stat-value">{Math.round(stats.progressPercentage)}%</div>
                        <div className="progress-bar">
                            <div 
                                className="progress-fill" 
                                style={{ width: `${stats.progressPercentage}%` }}
                            ></div>
                        </div>
                    </div>
                </div>

                <div className={`stat-card streak ${animateCards ? 'animate' : ''}`}>
                    <div className="stat-header">
                        <div className="stat-icon">
                            {React.createElement(getStreakIcon(getStreakInfo().streak), { size: 28, color: "#e74c3c" })}
                        </div>
                        <h3>Sequ√™ncia</h3>
                    </div>
                    <div className="stat-content">
                        <div className="stat-value">{getStreakInfo().streak} dias</div>
                        <div className="stat-change">{getStreakInfo().message}</div>
                    </div>
                </div>
            </div>

            {/* Achievement Badges */}
            {getAchievementBadges().length > 0 && (
                <div className="achievement-section">
                    <div className="section-header">
                        <TrophyIcon size={24} color="#f39c12" />
                        <h3>Conquistas</h3>
                    </div>
                    <div className="achievement-badges">
                        {getAchievementBadges().map((badge, index) => (
                            <div key={index} className="achievement-badge">
                                <div className="badge-icon">
                                    {React.createElement(badge.icon, { size: 20, color: "#27ae60" })}
                                </div>
                                <span className="badge-text">{badge.text}</span>
                            </div>
                        ))}
                    </div>
                </div>
            )}

            {/* Motivational Banner */}
            {showMotivation && (
                <div className="motivational-banner">
                    <div className="motivation-content">
                        <button 
                            className="close-motivation"
                            onClick={() => setShowMotivation(false)}
                            aria-label="Fechar mensagem"
                        >
                            <CloseIcon size={20} />
                        </button>
                        <div className="motivation-header">
                            <HeartIcon size={24} color="#e74c3c" />
                            <h3>Mensagem do Dia</h3>
                        </div>
                        <p>{getMotivationalMessage()}</p>
                    </div>
                </div>
            )}

            {/* Quick Charts Preview */}
            {metrics.length >= 2 && (
                <div className="charts-preview">
                    <div className="section-header">
                        <TrendingUpIcon size={24} color="#3498db" />
                        <h3>Evolu√ß√£o Recente</h3>
                    </div>
                    <div className="mini-charts-grid">
                        <div className="mini-chart-card">
                            <h4>Peso</h4>
                            <EvolutionChart 
                                data={metrics.slice(-5)} 
                                metric="weight" 
                                title="Peso (kg)" 
                                color="#3498db" 
                                unit="kg"
                                height={120}
                            />
                        </div>
                        {metrics.some(m => m.bodyFatPercentage) && (
                            <div className="mini-chart-card">
                                <h4>% Gordura</h4>
                                <EvolutionChart 
                                    data={metrics.filter(m => m.bodyFatPercentage).slice(-5)} 
                                    metric="bodyFatPercentage" 
                                    title="Gordura (%)" 
                                    color="#e74c3c" 
                                    unit="%"
                                    height={120}
                                />
                            </div>
                        )}
                    </div>
                </div>
            )}

            {/* Quick Actions Panel */}
            <div className="quick-actions-panel">
                <div className="section-header">
                    <BoltIcon size={24} color="#9b59b6" />
                    <h3>A√ß√µes R√°pidas</h3>
                </div>
                <div className="quick-actions-grid">
                    <button className="quick-action-btn metrics" onClick={() => handleTabChange('metrics')}>
                        <div className="action-icon">
                            <ChartIcon size={24} />
                        </div>
                        <div className="action-content">
                            <span className="action-text">Ver M√©tricas</span>
                            <span className="action-desc">Hist√≥rico completo</span>
                        </div>
                    </button>
                    <button className="quick-action-btn workouts" onClick={() => handleTabChange('workouts')}>
                        <div className="action-icon">
                            <DumbbellIcon size={24} />
                        </div>
                        <div className="action-content">
                            <span className="action-text">Meus Treinos</span>
                            <span className="action-desc">Planos ativos</span>
                        </div>
                    </button>
                    <button className="quick-action-btn goals" onClick={() => handleTabChange('goals')}>
                        <div className="action-icon">
                            <FlagIcon size={24} />
                        </div>
                        <div className="action-content">
                            <span className="action-text">Metas</span>
                            <span className="action-desc">Objetivos pessoais</span>
                        </div>
                    </button>
                </div>
            </div>
        </div>
    );

    const renderMetricsTab = () => (
        <div className="metrics-content">
            <div className="section-header">
                <ChartIcon size={24} color="#3498db" />
                <h2>Hist√≥rico de M√©tricas</h2>
                <p>Acompanhe sua evolu√ß√£o f√≠sica ao longo do tempo</p>
            </div>
            
            {metrics.length >= 2 ? (
                <div className="charts-grid">
                    <div className="chart-card">
                        <h3>Evolu√ß√£o do Peso</h3>
                        <EvolutionChart 
                            data={metrics} 
                            metric="weight" 
                            title="Peso (kg)" 
                            color="#3498db" 
                            unit="kg"
                        />
                    </div>
                    
                    {metrics.some(m => m.bodyFatPercentage) && (
                        <div className="chart-card">
                            <h3>% Gordura Corporal</h3>
                            <EvolutionChart 
                                data={metrics.filter(m => m.bodyFatPercentage)} 
                                metric="bodyFatPercentage" 
                                title="Gordura (%)" 
                                color="#e74c3c" 
                                unit="%"
                            />
                        </div>
                    )}
                    
                    {metrics.some(m => m.muscleMassPercentage) && (
                        <div className="chart-card">
                            <h3>% Massa Muscular</h3>
                            <EvolutionChart 
                                data={metrics.filter(m => m.muscleMassPercentage)} 
                                metric="muscleMassPercentage" 
                                title="M√∫sculo (%)" 
                                color="#27ae60" 
                                unit="%"
                            />
                        </div>
                    )}
                </div>
            ) : (
                <div className="empty-state">
                    <div className="empty-icon">
                        <ChartIcon size={48} color="#95a5a6" />
                    </div>
                    <h3>Dados insuficientes para gr√°ficos</h3>
                    <p>Adicione mais medi√ß√µes para visualizar sua evolu√ß√£o</p>
                </div>
            )}

            <div className="metrics-table-section">
                <div className="section-header">
                    <CalendarIcon size={24} color="#9b59b6" />
                    <h3>Detalhamento por Per√≠odo</h3>
                </div>
                <MetricsHistory metrics={metrics} />
            </div>
        </div>
    );

    const renderWorkoutsTab = () => (
        <div className="workouts-content">
            <div className="section-header">
                <DumbbellIcon size={24} color="#e67e22" />
                <h2>Meus Treinos</h2>
                <p>Acompanhe seus planos de treino e exerc√≠cios</p>
            </div>
            
            <WorkoutHistory clientId={clientData?._id || clientData?.id} />
        </div>
    );

    const renderGoalsTab = () => (
        <div className="goals-content">
            <div className="section-header">
                <FlagIcon size={24} color="#27ae60" />
                <h2>Metas e Objetivos</h2>
                <p>Defina e acompanhe seus objetivos pessoais</p>
            </div>
            
            <div className="goals-grid">
                <div className="goal-card weight-goal">
                    <div className="goal-header">
                        <div className="goal-icon">
                            <WeightIcon size={24} color="#3498db" />
                        </div>
                        <h3>Meta de Peso</h3>
                    </div>
                    <div className="goal-progress">
                        <span>Atual: {latestMetrics ? `${Math.round(latestMetrics.weight)} kg` : 'N/A'}</span>
                        <span>Meta: 75 kg</span>
                    </div>
                    <div className="goal-progress-bar">
                        <div className="goal-fill" style={{ width: '65%' }}></div>
                    </div>
                    <div className="goal-status active">65% conclu√≠do</div>
                </div>

                <div className="goal-card fat-goal">
                    <div className="goal-header">
                        <div className="goal-icon">
                            <DumbbellIcon size={24} color="#e74c3c" />
                        </div>
                        <h3>Meta de Gordura</h3>
                    </div>
                    <div className="goal-progress">
                        <span>Atual: {latestMetrics?.bodyFatPercentage ? `${Math.round(latestMetrics.bodyFatPercentage)}%` : 'N/A'}</span>
                        <span>Meta: 15%</span>
                    </div>
                    <div className="goal-progress-bar">
                        <div className="goal-fill" style={{ width: '40%' }}></div>
                    </div>
                    <div className="goal-status active">40% conclu√≠do</div>
                </div>

                <div className="goal-card workout-goal">
                    <div className="goal-header">
                        <div className="goal-icon">
                            <CalendarIcon size={24} color="#9b59b6" />
                        </div>
                        <h3>Treinos por Semana</h3>
                    </div>
                    <div className="goal-progress">
                        <span>Atual: 3x/semana</span>
                        <span>Meta: 4x/semana</span>
                    </div>
                    <div className="goal-progress-bar">
                        <div className="goal-fill" style={{ width: '75%' }}></div>
                    </div>
                    <div className="goal-status almost">75% conclu√≠do</div>
                </div>

                <div className="goal-card consistency-goal">
                    <div className="goal-header">
                        <div className="goal-icon">
                            <FireIcon size={24} color="#f39c12" />
                        </div>
                        <h3>Consist√™ncia</h3>
                    </div>
                    <div className="goal-progress">
                        <span>Sequ√™ncia: {getStreakInfo().streak} dias</span>
                        <span>Meta: 30 dias</span>
                    </div>
                    <div className="goal-progress-bar">
                        <div className="goal-fill" style={{ width: `${Math.min((getStreakInfo().streak / 30) * 100, 100)}%` }}></div>
                    </div>
                    <div className="goal-status active">{Math.min(Math.round((getStreakInfo().streak / 30) * 100), 100)}% conclu√≠do</div>
                </div>
            </div>

            {/* Goal Tips */}
            <div className="goal-tips">
                <div className="section-header">
                    <LightbulbIcon size={24} color="#f39c12" />
                    <h3>Dicas para Alcan√ßar suas Metas</h3>
                </div>
                <div className="tips-grid">
                    <div className="tip-card">
                        <div className="tip-icon">
                            <TargetIcon size={20} color="#27ae60" />
                        </div>
                        <p>Seja espec√≠fico com suas metas e defina prazos realistas</p>
                    </div>
                    <div className="tip-card">
                        <div className="tip-icon">
                            <PhoneIcon size={20} color="#3498db" />
                        </div>
                        <p>Registre seu progresso regularmente para manter o foco</p>
                    </div>
                    <div className="tip-card">
                        <div className="tip-icon">
                            <GroupIcon size={20} color="#9b59b6" />
                        </div>
                        <p>Converse com seu treinador sobre ajustes nos objetivos</p>
                    </div>
                </div>
            </div>
        </div>
    );

    if (loading) {
        return (
            <div className="my-progress-page">
                <div className="loading-container">
                    <div className="loading-spinner"></div>
                    <p>Carregando seu progresso...</p>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="my-progress-page">
                <div className="error-banner">
                    <span>‚ö†Ô∏è</span>
                    <span>Erro: {error}</span>
                </div>
            </div>
        );
    }

    const latestMetrics = metrics.length > 0 ? metrics[metrics.length - 1] : null;
    const currentBMI = latestMetrics ? calculateBMI(latestMetrics.weight, latestMetrics.height) : null;

    return (
        <div className="my-progress-page modern">
            {/* Hero Header */}
            <div className="hero-header">
                <div className="hero-content">
                    <div className="user-welcome">
                        <div className="avatar-section">
                            <div className="avatar-circle large">
                                {(clientData?.name || user?.name)?.charAt(0).toUpperCase()}
                            </div>
                            <div className="user-info">
                                <h1>{getTimeGreeting()}, {clientData?.name || user?.name}! üëã</h1>
                                <p className="hero-subtitle">Vamos acompanhar seu progresso hoje</p>
                                <div className="current-time">
                                    {currentTime.toLocaleString('pt-BR', { 
                                        weekday: 'long', 
                                        day: 'numeric', 
                                        month: 'long',
                                        hour: '2-digit',
                                        minute: '2-digit'
                                    })}
                                </div>
                            </div>
                        </div>
                        <div className="streak-badge">
                            <div className="streak-icon">
                                <FireIcon size={20} color="#fff" />
                            </div>
                            <span className="streak-text">{getStreakInfo().streak} dias</span>
                        </div>
                    </div>
                </div>
            </div>

            {/* Navigation Tabs */}
            <div className="tab-navigation">
                <div className="tab-buttons">
                    <button 
                        className={`tab-button ${activeTab === 'overview' ? 'active' : ''}`}
                        onClick={() => handleTabChange('overview')}
                    >
                        <span className="tab-icon">ÔøΩ</span>
                        Vis√£o Geral
                    </button>
                    <button 
                        className={`tab-button ${activeTab === 'metrics' ? 'active' : ''}`}
                        onClick={() => handleTabChange('metrics')}
                    >
                        <span className="tab-icon">üìä</span>
                        M√©tricas
                    </button>
                    <button 
                        className={`tab-button ${activeTab === 'workouts' ? 'active' : ''}`}
                        onClick={() => handleTabChange('workouts')}
                    >
                        <span className="tab-icon">ÔøΩ</span>
                        Treinos
                    </button>
                    <button 
                        className={`tab-button ${activeTab === 'goals' ? 'active' : ''}`}
                        onClick={() => handleTabChange('goals')}
                    >
                        <span className="tab-icon">üéØ</span>
                        Metas
                    </button>
                </div>
            </div>

            {/* Tab Content */}
            <div className="tab-content">
                {renderTabContent()}
            </div>
        </div>
    );
};

export default MyProgressPage;
