import { Link } from 'react-router-dom';

const TrainerDashboardPage = () => {
    const { user } = useContext(AuthContext);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [stats, setStats] = useState({
        totalClients: 0,
        activeClients: 0,
        totalWorkouts: 0,
        monthlyProgress: 0
    });
    const [clients, setClients] = useState([]);
    const [recentActivities, setRecentActivities] = useState([]);

    useEffect(() => {
        if (user && user.role === 'trainer') {
            fetchTrainerData();
        }
    }, [user]);

    const fetchTrainerData = async () => {
        try {
            const token = localStorage.getItem('token');
            
            // Buscar clientes do treinador
            const clientsResponse = await fetch('http://localhost:5000/api/trainer/my-clients', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                }
            });

            if (clientsResponse.ok) {
                const clientsData = await clientsResponse.json();
                setClients(clientsData);
                
                // Calcular estat√≠sticas
                setStats({
                    totalClients: clientsData.length,
                    activeClients: clientsData.filter(client => client.isActive !== false).length,
                    totalWorkouts: clientsData.reduce((sum, client) => sum + (client.workoutCount || 0), 0),
                    monthlyProgress: 85 // Placeholder - calcular progresso real
                });
            }

            // Buscar atividades recentes (placeholder)
            setRecentActivities([
                {
                    id: 1,
                    type: 'workout_completed',
                    clientName: 'Jo√£o Pereira',
                    description: 'Completou treino de muscula√ß√£o',
                    date: new Date(),
                    icon: 'üí™'
                },
                {
                    id: 2,
                    type: 'progress_update',
                    clientName: 'Maria Silva',
                    description: 'Atualizou m√©tricas corporais',
                    date: new Date(Date.now() - 2 * 60 * 60 * 1000),
                    icon: 'üìä'
                },
                {
                    id: 3,
                    type: 'goal_achieved',
                    clientName: 'Pedro Santos',
                    description: 'Atingiu meta de peso',
                    date: new Date(Date.now() - 24 * 60 * 60 * 1000),
                    icon: 'üéØ'
                }
            ]);

        } catch (err) {
            setError('Erro ao carregar dados do treinador');
            console.error('Erro:', err);
        } finally {
            setLoading(false);
        }
    };

    const formatDate = (date) => {
        const now = new Date();
        const diffMs = now - date;
        const diffMins = Math.floor(diffMs / 60000);
        const diffHours = Math.floor(diffMs / 3600000);
        const diffDays = Math.floor(diffMs / 86400000);

        if (diffMins < 60) return `${diffMins}min atr√°s`;
        if (diffHours < 24) return `${diffHours}h atr√°s`;
        return `${diffDays}d atr√°s`;
    };

    if (!user || user.role !== 'trainer') {
        return (
            <div className="error-page">
                <h2>Acesso Negado</h2>
                <p>Esta p√°gina √© exclusiva para personal trainers.</p>
            </div>
        );
    }

    if (loading) {
        return (
            <div className="loading-container">
                <div className="loading-spinner"></div>
                <p>Carregando dashboard...</p>
            </div>
        );
    }

    return (
        <div className="trainer-dashboard-page">
            {/* Header */}
            <div className="dashboard-header">
                <div className="header-content">
                    <div className="trainer-info">
                        <h1>Dashboard Personal Trainer</h1>
                        <p className="welcome-text">
                            Bem-vindo, <strong>{user.profile?.firstName || user.username}</strong>!
                        </p>
                        <p className="subtitle">Gerencie seus clientes e acompanhe o progresso</p>
                    </div>
                    <div className="quick-actions">
                        <Link to="/workout-plan/new" className="btn btn-primary">
                            <span className="icon">‚ûï</span>
                            Novo Treino
                        </Link>
                        <Link to="/clients/new" className="btn btn-secondary">
                            <span className="icon">üë•</span>
                            Adicionar Cliente
                        </Link>
                    </div>
                </div>
            </div>

            {error && (
                <div className="error-banner">
                    <span className="error-icon">‚ö†Ô∏è</span>
                    {error}
                </div>
            )}

            {/* Stats Cards */}
            <div className="stats-grid">
                <div className="stat-card primary">
                    <div className="stat-icon">üë•</div>
                    <div className="stat-content">
                        <h3>{stats.totalClients}</h3>
                        <p>Total de Clientes</p>
                    </div>
                </div>
                <div className="stat-card success">
                    <div className="stat-icon">‚úÖ</div>
                    <div className="stat-content">
                        <h3>{stats.activeClients}</h3>
                        <p>Clientes Ativos</p>
                    </div>
                </div>
                <div className="stat-card warning">
                    <div className="stat-icon">üí™</div>
                    <div className="stat-content">
                        <h3>{stats.totalWorkouts}</h3>
                        <p>Treinos Criados</p>
                    </div>
                </div>
                <div className="stat-card info">
                    <div className="stat-icon">üìà</div>
                    <div className="stat-content">
                        <h3>{stats.monthlyProgress}%</h3>
                        <p>Progresso Mensal</p>
                    </div>
                </div>
            </div>

            {/* Main Content */}
            <div className="dashboard-content">
                {/* Clients Section */}
                <div className="section clients-section">
                    <div className="section-header">
                        <h2>Meus Clientes</h2>
                        <Link to="/clients" className="view-all-link">
                            Ver todos <span className="arrow">‚Üí</span>
                        </Link>
                    </div>
                    
                    {clients.length === 0 ? (
                        <div className="empty-state">
                            <div className="empty-icon">üë•</div>
                            <h3>Nenhum cliente ainda</h3>
                            <p>Comece adicionando seus primeiros clientes para gerenciar seus treinos</p>
                            <Link to="/clients/new" className="btn btn-primary">
                                Adicionar Primeiro Cliente
                            </Link>
                        </div>
                    ) : (
                        <div className="clients-grid">
                            {clients.slice(0, 6).map(client => (
                                <div key={client._id} className="client-card">
                                    <div className="client-avatar">
                                        {client.name.charAt(0).toUpperCase()}
                                    </div>
                                    <div className="client-info">
                                        <h4>{client.name}</h4>
                                        <p className="client-email">{client.email}</p>
                                        <p className="client-phone">{client.phone}</p>
                                    </div>
                                    <div className="client-status">
                                        <span className={`status-badge ${client.isActive !== false ? 'active' : 'inactive'}`}>
                                            {client.isActive !== false ? 'Ativo' : 'Inativo'}
                                        </span>
                                    </div>
                                    <div className="client-actions">
                                        <Link 
                                            to={`/clients/${client._id}`} 
                                            className="btn btn-small btn-outline"
                                        >
                                            Ver Perfil
                                        </Link>
                                        <Link 
                                            to={`/workout-plan/new?client=${client._id}`} 
                                            className="btn btn-small btn-primary"
                                        >
                                            Novo Treino
                                        </Link>
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}
                </div>

                {/* Recent Activities */}
                <div className="section activities-section">
                    <div className="section-header">
                        <h2>Atividades Recentes</h2>
                        <Link to="/activities" className="view-all-link">
                            Ver todas <span className="arrow">‚Üí</span>
                        </Link>
                    </div>
                    
                    <div className="activities-list">
                        {recentActivities.map(activity => (
                            <div key={activity.id} className="activity-item">
                                <div className="activity-icon">{activity.icon}</div>
                                <div className="activity-content">
                                    <p className="activity-description">
                                        <strong>{activity.clientName}</strong> {activity.description}
                                    </p>
                                    <span className="activity-time">{formatDate(activity.date)}</span>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>

            {/* Quick Stats */}
            <div className="quick-stats">
                <div className="quick-stat">
                    <h3>Esta Semana</h3>
                    <div className="stat-items">
                        <div className="stat-item">
                            <span className="label">Treinos Conclu√≠dos</span>
                            <span className="value">24</span>
                        </div>
                        <div className="stat-item">
                            <span className="label">Novos Clientes</span>
                            <span className="value">3</span>
                        </div>
                        <div className="stat-item">
                            <span className="label">Avalia√ß√µes Positivas</span>
                            <span className="value">18</span>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default TrainerDashboardPage;
