import React, { useState, useEffect } from 'react';
import { getWorkoutPlans, createWorkoutPlan, updateWorkoutPlan } from '../../../services/workout.service';

const WorkoutPlan = () => {
    const [workoutPlans, setWorkoutPlans] = useState([]);
    const [newPlan, setNewPlan] = useState({ name: '', exercises: [] });
    const [editingPlan, setEditingPlan] = useState(null);

    useEffect(() => {
        fetchWorkoutPlans();
    }, []);

    const fetchWorkoutPlans = async () => {
        const plans = await getWorkoutPlans();
        setWorkoutPlans(plans);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewPlan({ ...newPlan, [name]: value });
    };

    const handleAddPlan = async () => {
        if (editingPlan) {
            await updateWorkoutPlan(editingPlan.id, newPlan);
        } else {
            await createWorkoutPlan(newPlan);
        }
        setNewPlan({ name: '', exercises: [] });
        setEditingPlan(null);
        fetchWorkoutPlans();
    };

    const handleEditPlan = (plan) => {
        setNewPlan(plan);
        setEditingPlan(plan);
    };

    const handleDeletePlan = async (id) => {
        // Implement delete functionality
    };

    return (
        <div>
            <h2>Workout Plans</h2>
            <input
                type="text"
                name="name"
                value={newPlan.name}
                onChange={handleInputChange}
                placeholder="Workout Plan Name"
            />
            <button onClick={handleAddPlan}>
                {editingPlan ? 'Update Plan' : 'Add Plan'}
            </button>
            <ul>
                {workoutPlans.map(plan => (
                    <li key={plan.id}>
                        {plan.name}
                        <button onClick={() => handleEditPlan(plan)}>Edit</button>
                        <button onClick={() => handleDeletePlan(plan.id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default WorkoutPlan;
