import React, { useEffect, useState } from 'react';
import * as workoutService from '../../services/workout.service';

const WorkoutHistory = ({ clientId }) => {
    const [workouts, setWorkouts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchWorkoutHistory = async () => {
            if (!clientId) {
                setError('ID do cliente n√£o fornecido');
                setLoading(false);
                return;
            }

            try {
                console.log('üèãÔ∏è Buscando hist√≥rico de treinos para cliente:', clientId);
                const response = await workoutService.getWorkoutsByClientId(clientId);
                console.log('üèãÔ∏è Treinos encontrados:', response);
                setWorkouts(Array.isArray(response) ? response : response.data || []);
            } catch (err) {
                console.error('‚ùå Erro ao buscar hist√≥rico de treinos:', err);
                setError('Erro ao carregar hist√≥rico de treinos');
            } finally {
                setLoading(false);
            }
        };

        fetchWorkoutHistory();
    }, [clientId]);

    if (loading) {
        return (
            <div className="loading-container">
                <div className="loading-spinner"></div>
                <p>Carregando hist√≥rico de treinos...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div className="error-container">
                <span className="error-icon">‚ö†Ô∏è</span>
                <p>{error}</p>
            </div>
        );
    }

    if (!workouts || workouts.length === 0) {
        return (
            <div className="empty-state">
                <div className="empty-icon">üèãÔ∏è</div>
                <h4>Nenhum treino encontrado</h4>
                <p>Este cliente ainda n√£o possui treinos cadastrados.</p>
            </div>
        );
    }

    return (
        <div className="workout-history">
            <div className="workouts-list">
                {workouts.map((workout, index) => (
                    <div key={workout._id || index} className="workout-item">
                        <div className="workout-header">
                            <h4>{workout.name || workout.title || `Treino ${index + 1}`}</h4>
                            <span className="workout-date">
                                {workout.createdAt 
                                    ? new Date(workout.createdAt).toLocaleDateString('pt-BR')
                                    : 'Data n√£o informada'
                                }
                            </span>
                        </div>
                        {workout.description && (
                            <p className="workout-description">{workout.description}</p>
                        )}
                        {workout.exercises && workout.exercises.length > 0 && (
                            <div className="workout-exercises">
                                <span className="exercises-count">
                                    {workout.exercises.length} exerc√≠cio(s)
                                </span>
                            </div>
                        )}
                        {workout.difficulty && (
                            <span className={`difficulty-badge ${workout.difficulty.toLowerCase()}`}>
                                {workout.difficulty}
                            </span>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default WorkoutHistory;
