import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../contexts/AuthContext';
import { Link } from 'react-router-dom';

const ActivitiesPage = () => {
    const { user } = useContext(AuthContext);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [activities, setActivities] = useState([]);
    const [filter, setFilter] = useState('all'); // all, workouts, progress, clients

    useEffect(() => {
        if (user && user.role === 'trainer') {
            fetchActivities();
        }
    }, [user]);

    const fetchActivities = async () => {
        try {
            const token = localStorage.getItem('token');
            
            const response = await fetch('http://localhost:5000/api/trainer/recent-activities', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                }
            });

            if (response.ok) {
                const data = await response.json();
                setActivities(data);
            } else {
                console.error('Erro ao buscar atividades:', response.status);
                setError('Erro ao carregar atividades');
            }
        } catch (err) {
            console.error('Erro:', err);
            setError('Erro ao carregar atividades');
        } finally {
            setLoading(false);
        }
    };

    const formatDate = (date) => {
        const activityDate = new Date(date);
        const now = new Date();
        const diffMs = now - activityDate;
        const diffMins = Math.floor(diffMs / 60000);
        const diffHours = Math.floor(diffMs / 3600000);
        const diffDays = Math.floor(diffMs / 86400000);

        if (diffMins < 60) return `${diffMins} minutos atrás`;
        if (diffHours < 24) return `${diffHours} horas atrás`;
        if (diffDays === 1) return 'Ontem';
        if (diffDays < 7) return `${diffDays} dias atrás`;
        
        return activityDate.toLocaleDateString('pt-BR', {
            day: '2-digit',
            month: '2-digit',
            year: 'numeric'
        });
    };

    const getFilteredActivities = () => {
        if (filter === 'all') return activities;
        return activities.filter(activity => activity.type === filter);
    };

    if (!user || user.role !== 'trainer') {
        return (
            <div className="error-page">
                <h2>Acesso Negado</h2>
                <p>Esta página é exclusiva para personal trainers.</p>
                <Link to="/login" className="btn btn-primary">Fazer Login</Link>
            </div>
        );
    }

    if (loading) {
        return (
            <div className="loading-container">
                <div className="loading-spinner"></div>
                <p>Carregando atividades...</p>
            </div>
        );
    }

    return (
        <div className="activities-page">
            {/* Header */}
            <div className="page-header">
                <div className="header-content">
                    <div className="breadcrumb">
                        <Link to="/dashboard" className="breadcrumb-link">Dashboard</Link>
                        <span className="breadcrumb-separator">→</span>
                        <span className="breadcrumb-current">Atividades</span>
                    </div>
                    <h1>Atividades Recentes</h1>
                    <p className="page-subtitle">
                        Acompanhe todas as atividades dos seus clientes em um só lugar
                    </p>
                </div>
            </div>

            {error && (
                <div className="error-banner">
                    <span className="error-icon">⚠️</span>
                    {error}
                </div>
            )}

            {/* Filters */}
            <div className="filters-section">
                <div className="filter-buttons">
                    <button 
                        className={`filter-btn ${filter === 'all' ? 'active' : ''}`}
                        onClick={() => setFilter('all')}
                    >
                        Todas
                    </button>
                    <button 
                        className={`filter-btn ${filter === 'workout' ? 'active' : ''}`}
                        onClick={() => setFilter('workout')}
                    >
                        Treinos
                    </button>
                    <button 
                        className={`filter-btn ${filter === 'progress' ? 'active' : ''}`}
                        onClick={() => setFilter('progress')}
                    >
                        Progresso
                    </button>
                    <button 
                        className={`filter-btn ${filter === 'client' ? 'active' : ''}`}
                        onClick={() => setFilter('client')}
                    >
                        Clientes
                    </button>
                </div>
            </div>

            {/* Activities List */}
            <div className="activities-content">
                {getFilteredActivities().length === 0 ? (
                    <div className="empty-state">
                        <div className="empty-icon">📋</div>
                        <h3>Nenhuma atividade encontrada</h3>
                        <p>
                            {filter === 'all' 
                                ? 'Ainda não há atividades registradas. As atividades dos seus clientes aparecerão aqui conforme eles usam o sistema.'
                                : `Nenhuma atividade do tipo "${filter}" encontrada.`
                            }
                        </p>
                        <Link to="/clients" className="btn btn-primary">
                            Ver Meus Clientes
                        </Link>
                    </div>
                ) : (
                    <div className="activities-timeline">
                        {getFilteredActivities().map((activity, index) => (
                            <div key={activity.id || index} className="activity-timeline-item">
                                <div className="activity-timeline-marker">
                                    <div className="activity-icon">{activity.icon}</div>
                                </div>
                                <div className="activity-timeline-content">
                                    <div className="activity-card">
                                        <div className="activity-header">
                                            <h4 className="activity-title">
                                                <strong>{activity.clientName}</strong> {activity.description}
                                            </h4>
                                            <span className="activity-time">
                                                {formatDate(activity.date)}
                                            </span>
                                        </div>
                                        {activity.details && (
                                            <p className="activity-details">{activity.details}</p>
                                        )}
                                        {activity.clientId && (
                                            <div className="activity-actions">
                                                <Link 
                                                    to={`/clients/${activity.clientId}`} 
                                                    className="btn btn-small btn-outline"
                                                >
                                                    Ver Perfil
                                                </Link>
                                            </div>
                                        )}
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default ActivitiesPage;
