import React, { createContext, useState, useCallback, useContext } from 'react';
import * as clientService from '../services/client.service';
import { AuthContext } from './AuthContext';

export const ClientContext = createContext();

export const ClientProvider = ({ children }) => {
    const [clients, setClients] = useState([]);
    const [selectedClient, setSelectedClient] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const fetchClients = useCallback(async () => {
        try {
            setLoading(true);
            setError(null);
            const data = await clientService.getClients();
            setClients(data);
        } catch (err) {
            console.error("Error fetching clients:", err);
            setError("Failed to load clients. Please try again later.");
        } finally {
            setLoading(false);
        }
    }, []);

    const fetchClientById = useCallback(async (clientId) => {
        try {
            setLoading(true);
            setError(null);
            const data = await clientService.getClientById(clientId);
            setSelectedClient(data);
            return data;
        } catch (err) {
            console.error(`Error fetching client ${clientId}:`, err);
            setError("Failed to load client details. Please try again later.");
            return null;
        } finally {
            setLoading(false);
        }
    }, []);

    const addClient = async (clientData) => {
        try {
            setLoading(true);
            setError(null);
            const newClient = await clientService.createClient(clientData);
            setClients((prevClients) => [...prevClients, newClient]);
            return newClient;
        } catch (err) {
            console.error("Error adding client:", err);
            setError("Failed to add client. Please try again later.");
            return null;
        } finally {
            setLoading(false);
        }
    };

    const updateClient = async (clientId, updatedData) => {
        try {
            setLoading(true);
            setError(null);
            const updatedClient = await clientService.updateClient(clientId, updatedData);
            setClients((prevClients) =>
                prevClients.map((client) => (client._id === clientId ? updatedClient : client))
            );

            if (selectedClient && selectedClient._id === clientId) {
                setSelectedClient(updatedClient);
            }

            return updatedClient;
        } catch (err) {
            console.error(`Error updating client ${clientId}:`, err);
            setError("Failed to update client. Please try again later.");
            return null;
        } finally {
            setLoading(false);
        }
    };

    const deleteClient = async (clientId) => {
        try {
            setLoading(true);
            setError(null);
            await clientService.deleteClient(clientId);
            setClients((prevClients) => prevClients.filter((client) => client._id !== clientId));

            if (selectedClient && selectedClient._id === clientId) {
                setSelectedClient(null);
            }

            return true;
        } catch (err) {
            console.error(`Error deleting client ${clientId}:`, err);
            setError("Failed to delete client. Please try again later.");
            return false;
        } finally {
            setLoading(false);
        }
    };

    return (
        <ClientContext.Provider 
            value={{ 
                clients, 
                selectedClient,
                loading, 
                error, 
                fetchClients, 
                fetchClientById,
                addClient, 
                updateClient, 
                deleteClient 
            }}
        >
            {children}
        </ClientContext.Provider>
    );
};
