import React, { useContext } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { AuthContext } from '../../contexts/AuthContext';
import UserDebugInfo from '../debug/UserDebugInfo';
import './Header.css';

const Header = () => {
    const { user, logout } = useContext(AuthContext) || { user: null, logout: () => {} };
    const history = useHistory();

    const handleLogout = async () => {
        try {
            await logout();
            // Força redirecionamento para login após logout
            history.push('/login');
        } catch (error) {
            console.error('Erro no logout:', error);
            // Mesmo se houver erro, redireciona para login
            history.push('/login');
        }
    };

    return (
        <>
            <UserDebugInfo />
            <header className="header">
                <div className="header-logo">
                    <h1>TrainerTrack</h1>
                </div>
                <div className="header-actions">
                    {user ? (
                        <div className="user-info">
                            <span className="username">Bem-vindo, {user.username || user.name || 'Usuário'}</span>
                            <span className="user-role">({user.role})</span>
                            <button className="logout-button" onClick={handleLogout}>
                                Sair
                            </button>
                        </div>
                    ) : (
                        <Link to="/login" className="login-button">
                            Entrar
                        </Link>
                    )}
                </div>
            </header>
        </>
    );
};

export default Header;
