import React, { useContext, useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { ClientContext } from '../contexts/ClientContext';
import clientService from '../services/client.service';
import MetricsHistory from '../components/metrics/MetricsHistory';
import WorkoutHistory from '../components/workouts/WorkoutHistory';

const ClientDetailPage = () => {
    const { clientId } = useParams();
    const { clients } = useContext(ClientContext);
    const [client, setClient] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchClientDetails = async () => {
            try {
                const response = await clientService.getClientById(clientId);
                setClient(response.data);
            } catch (err) {
                setError('Failed to fetch client details');
            } finally {
                setLoading(false);
            }
        };

        fetchClientDetails();
    }, [clientId]);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div>
            <h1>Client Details</h1>
            {client && (
                <div>
                    <h2>{client.name}</h2>
                    <p>Weight: {client.weight} kg</p>
                    <p>Height: {client.height} cm</p>
                    <p>Body Fat: {client.bodyFat}%</p>
                    <p>Muscle Mass: {client.muscleMass}%</p>
                    <h3>Metrics History</h3>
                    <MetricsHistory clientId={clientId} />
                    <h3>Workout History</h3>
                    <WorkoutHistory clientId={clientId} />
                </div>
            )}
        </div>
    );
};

export default ClientDetailPage;
